INSERT INTO `cms_article` (`id`, `user_id`, `category_id`, `subject`, `message`, `image_name`, `digest`, `keyword`, `top`, `rate`, `rate_times`, `views`, `allow_comment`, `status`, `last_modified_date`, `created_date`, `deleted`) VALUES
(1, 1, 3, '北京用友政务领导和专家来本中心交流', '&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;2012&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;27&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;日下午，山东省金融信息工程技术研究中心与北京用友政务软件有限公司合作交流会在本中心举行。中心主任徐如志教授会见了用友政务的总经理周海樯一行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;会见中，徐如志主任介绍了本中心的基本情况，着重突出了山东财经大学的优势学科，本中心将计算机技术与金融、财税结合的特点，并阐明了本中心&ldquo;产学研&rdquo;一体化的定位，表达了与社会企业开放合作的意愿。用友政务的周海樯总经理也介绍了用友政务的发展概况，业务优势，以及在企业发展中遇到的一些困惑和难题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;双方的专家和教授就税务信息化方面的热点问题，展开了具有前瞻性和建设性的讨论。最后，双方本着合作共赢、开放发展的理念，就服务支持、客户培训、高端咨询、共建技术平台、合作研究等合作事项，进行了深入的探讨，对于未来的合作达成了初步的共识。&lt;/span&gt;&lt;/p&gt;', '1334474546620.png', '    2012年3月27日下午，山东省金融信息工程技术研究中心与北京用友政务软件有限公司合作交流会在本中心举行。中心主任徐如志教授会见了用友政务的总经理周海樯一行。     会见中，徐如志主任介绍了本中心的基本情况，着重突出了山东财经大学的优势学科，本中心将计算机技术与金融、财税结合的特点，并阐明', '', 0, 0, 0, 14, 1, 1, '2012-04-28 05:41:28', '2012-04-14 18:01:51', 0),
(2, 1, 3, '与山东中孚信息产业股份有限公司见面交流会', '&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;2012&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;年&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;3&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;月&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;28&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;日，山东省金融信息工程技术中心与山东中孚信息产业股份有限公司在本中心会议室举行了合作交流会。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;交流会受到双方的高度重视。本中心主任徐如志率领聂秀山副教授、赵华伟副教授及办公室主任与中孚公司的领导和专家进行了友好的交流会谈。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;交流会伊始，由徐如志主任对山东财经大学的历史沿革、本中心的创建背景、研究方向以及目标定位等方面做了说明，并对近阶段中心已取得的科研成果进行了介绍。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;会上山东中孚和本中心就&lt;/span&gt;&lt;span style=&quot;font-size:19px;background-color:white&quot;&gt;面向金融、财税部门的信息安全培训、咨询等业务的开展，成立信息安全联合实验室以及人才培养&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;等合作事项进行了建设性的探讨。&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-align:left;&quot;&gt;&lt;span style=&quot;font-size:19px&quot;&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;此次会议，增进了双方的友谊和了解，为今后打造更优秀的产学研一体的金融信息工程技术研究中心奠定了良好的基础。&lt;/span&gt;&lt;/p&gt;', '1334474546620.png', '&nbsp;&nbsp;&nbsp;&nbsp;2012年3月28日，山东省金融信息工程技术中心与山东中孚信息产业股份有限公司在本中心会议室举行了合作交流会。 &nbsp;&nbsp;&nbsp;&nbsp;交流会受到双方的高度重视。本中心主任徐如志率领聂秀山副教授、赵华伟副教授及办公室主任与中孚公司的领导和专家进行了友好的交流会谈。 交流会伊始，由徐如志主任对山东财经大学的历', '', 0, 0, 0, 7, 1, 1, '2012-04-14 22:05:21', '2012-04-14 19:32:49', 0),
(3, 1, 7, '中心简介', '&lt;p style=&quot;text-indent:28px;line-height:180%;text-align:left;&quot;&gt;山东省金融信息工程技术研究中心（以下简称&ldquo;中心&rdquo;）依托山东财经大学的金融、财税、会计、管理、数学和信息技术等雄厚的学科资源，联合行业内相关优势企业，为推动我国金融信息化的发展及相关技术的研究而成立的一个开放的专业从事与金融信息化和金融风险管理相关的理论与应用技术研究、教育培训与咨询服务的组织。中心于2010年12月经山东省科技厅批准成立，是省内在金融信息化行业领域唯一的省级工程技术研究中心。&lt;/p&gt;&lt;p style=&quot;text-indent:28px;line-height:180%;text-align:left;&quot;&gt;中心下设计算金融研究所、金融风险管理研究所、金融信息安全研究所等若干个在业务上相对独立的研究所；设金融智能实验室等以理论和技术研究为主的专业实验室与研究室。主要研究金融信息化相关的金融系统分析与设计、信息安全与应急管理、金融信息处理与金融商务智能应用技术及工具。主要的研究方向包括金融服务云计算与应用集成、金融IT风险管理、金融商务智能与客户挖掘、金融信息安全及应急处置技术和金融信用评级系统等。&lt;/p&gt;&lt;p style=&quot;text-indent:28px;line-height:180%;text-align:left;&quot;&gt;目前中心与山东省城商联盟、齐鲁证券等多家金融机构、北京用友、山东舜德数据及加拿大Goitsys等多家国内外企业建立了长期的紧密合作关系。已形成一支由30余人组成的研发团队，其中&ldquo;国家千人计划&rdquo;特聘专家1人，18名博士中5人为来自清华大学、上海交通大学、澳大利亚昆士兰大学、美国Texas州立大学的博士后。独立研究及合作研发的多项成果已成功应用于交通银行、广发银行、恒丰银行、齐鲁银行、德州银行及小额贷款公司等非银行金融机构。2011年获批4项国家自然基金项目4项资助，申报国家发明专利3项。&lt;/p&gt;', '', '山东省金融信息工程技术研究中心（以下简称&ldquo;中心&rdquo;）依托山东财经大学的金融、财税、会计、管理、数学和信息技术等雄厚的学科资源，联合行业内相关优势企业，为推动我国金融信息化的发展及相关技术的研究而成立的一个开放的专业从事与金融信息化和金融风险管理相关的理论与应用技术研究、教育培训与咨询服务的组织。中心于', '', 1, 0, 0, 28, 0, 1, '2012-04-28 13:39:37', '2012-04-14 23:00:56', 0),
(4, 1, 8, '组织结构', '&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;中心下设计算金融研究所、金融风险管理研究所、金融信息安全研究所等若干个在业务上相对独立的研究所；设金融智能实验室等以理论和技术研究为主的专业实验室与研究室。主要研究金融信息化相关的金融系统分析与设计、信息安全与应急管理、金融信息处理与金融商务智能应用技术及工具。研究方向包括金融服务云计算与应用集成、金融IT风险管理、金融商务智能与客户挖掘、金融信息安全及应急处置技术和金融信用评级系统等。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1335677157051-SuroEr.jpg&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; width=&quot;600&quot; height=&quot;337&quot; style=&quot;width:600px;height:337px;&quot; /&gt;&lt;/p&gt;', '1335677157051-SuroEr.jpg', '中心下设计算金融研究所、金融风险管理研究所、金融信息安全研究所等若干个在业务上相对独立的研究所；设金融智能实验室等以理论和技术研究为主的专业实验室与研究室。主要研究金融信息化相关的金融系统分析与设计、信息安全与应急管理、金融信息处理与金融商务智能应用技术及工具。研究方向包括金融服务云计算与应用集成、', '', 1, 0, 0, 40, 0, 1, '2012-05-05 10:08:42', '2012-04-14 15:23:22', 0),
(5, 1, 9, '运作机制', '&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;本中心实行理事会领导下的中心主任负责制。&lt;/p&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;按照《山东省工程技术研究中心管理办法》，实行项目管理，实行&ldquo;机构开放、人员流动、内外联合、竞争创新、&lsquo;产学研&rsquo;一体化&rdquo;的运行机制。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;ul style=&quot;list-style-type:&quot;&gt;&lt;li&gt;对外实行设备资源开放、研究项目开放、学术交流开放和人才使用开放。&lt;/li&gt;&lt;li&gt;建立良好的人才流动机制，吸引省内外科技人员携带科研成果进行成果转化。&lt;/li&gt;&lt;li&gt;在机构组建、项目申报、课题研究时强化&ldquo;产学院&rdquo;结合，充分发挥各方优势。&lt;/li&gt;&lt;li&gt;根据国家现行关于技术转让、知识产权等规定，建立互利互惠的开放合作机制。以工程技术研究中心作为科技成果向生产力转化的中间环节，推动金融信息化科技成果向现实生产力的转化。&lt;/li&gt;&lt;/ul&gt;', '', '本中心实行理事会领导下的中心主任负责制。 按照《山东省工程技术研究中心管理办法》，实行项目管理，实行&ldquo;机构开放、人员流动、内外联合、竞争创新、&lsquo;产学研&rsquo;一体化&rdquo;的运行机制。 对外实行设备资源开放、研究项目开放、学术交流开放和人才使用开放。 建立良好的人才流动机制，吸引省内外科技人员携带科研成果进行成', '', 1, 0, 0, 5, 0, 1, '2012-04-28 09:43:58', '2012-04-14 23:27:53', 0),
(6, 1, 12, '学术带头人', '&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;聂培尧教授，工学博士(计算机软件与理论)，博士生/硕士生导师，山东财政学院副院长，计算机信息工程学院教授，博士生指导教师，同时兼任天津大学管理学院博士生导师，山东大学计算机学院博士生导师。英国爱丁堡Napier大学商学院客座教授，美国纽约州立大学Old&amp;nbsp;Westbury学院访问教授。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;中心主任徐如志，博士、教授、博士生导师，美国ArizonaState&amp;nbsp;University访问教授，IEEE会员，中国云计算专家委员会委员，山东省计算机学会、管理学会常务理事。&amp;nbsp;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;中心副主任郭建峰，国家&ldquo;千人计划&rdquo;特聘专家，多伦多大学Rotman学院研究员；英国雷丁大学研究员，承担ICMA国际资本协会高频交易研究课题；山东财经大学计算金融研究所所长、特聘教授，山东财经大学金融学院金融学硕士生导师。\r\n&lt;/p&gt;\r\n&lt;p&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;br /&gt;\r\n&lt;/p&gt;', '', '&nbsp;&nbsp;&nbsp;&nbsp;聂培尧教授，工学博士(计算机软件与理论)，博士生/硕士生导师，山东财政学院副院长，计算机信息工程学院教授，博士生指导教师，同时兼任天津大学管理学院博士生导师，山东大学计算机学院博士生导师。英国爱丁堡Napier大学商学院客座教授，美国纽约州立大学Old&nbsp;Westbury学院访问教授。 &nbsp;&nbsp;&nbsp;', '', 1, 0, 0, 22, 0, 1, '2012-04-28 09:43:51', '2012-04-14 23:32:34', 0),
(7, 1, 13, '学术骨干', '&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;安起光教授，山东财经大学金融学院副院长。&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;田茂圣，山东舜德数据管理软件工程有限公司总经理。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;杨峰副教授，清华大学博士毕业，硕士生导师。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;赵志崑副教授，&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;工学博士，硕士生导师。澳大利亚中昆士兰大学博士后。多年来一直从事软件体系结构、智能&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;Agent&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;系统、金融信息系统等方面的研究。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;张抗抗副教授，&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;工学博士，硕士生导师，美国&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;Arizona&amp;nbsp;State&amp;nbsp;University&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;访问学者。多年来一直从事服务计算、信息集成与应用集成及金融云计算综合服务平台等方面的研究。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;林培光副教授，2006年毕业于北京理工大学，获工学博士学位，硕士生导师，香港科技大学访问学者。多年来一直从事Web数据集成及其相关应用以及金融信息管理系统、金融数据挖掘等方面的研究。&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;王帅强&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;赵华伟副教授&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;聂秀山&lt;span style=&quot;background-color:white&quot;&gt;博士，&lt;/span&gt;&lt;span style=&quot;background-color:white&quot;&gt;副教授，毕业于山东大学信息科学与工程学院。近年来主持国家自然基金一项，省两化融合研究课题一项，参与国家&lt;/span&gt;&lt;span style=&quot;font-family:&amp;#39;calibri&amp;#39;,&amp;#39;sans-serif&amp;#39;;background:white&quot;&gt;973&lt;/span&gt;&lt;span style=&quot;background-color:white&quot;&gt;项目和国家自然基金项目多项。主要研究方向为金融信息安全、信息内容安全认证等。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;王倩&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;张晶&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;马孝先&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;黄方亮&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;马玉林&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;谭璐&lt;/p&gt;&lt;p style=&quot;line-height:29px;text-indent:2em;text-align:left;margin-bottom:4px;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;30&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;余人的学术团队中有&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;18&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;名博士，其中&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;5&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;人为来自清华大学、上海交通大学、澳大利亚昆士兰大学、美国&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;Texas&lt;/span&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;州立大学的博士后。此外还有多名在业界具有丰富实战经验的业务咨询顾问和国内外的专兼职专家。&lt;/span&gt;&lt;/p&gt;', '', '安起光教授，山东财经大学金融学院副院长。 田茂圣，山东舜德数据管理软件工程有限公司总经理。 杨峰副教授，清华大学博士毕业，硕士生导师。 赵志崑副教授，工学博士，硕士生导师。澳大利亚中昆士兰大学博士后。多年来一直从事软件体系结构、智能Agent系统、金融信息系统等方面的研究。 张抗抗副教授，工学博士，', '', 1, 0, 0, 4, 0, 1, '2012-04-28 03:23:09', '2012-04-14 23:31:20', 0),
(8, 1, 16, '研究方向', '&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;本中心秉承合作共赢、开放发展的理念，以服务社会、满足市场需求为导向，将计算机技术与财税、金融等优势学科充分结合，与各银行、融资机构在以下四个方向进行战略性的合作研究：&lt;/p&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;（1）金融信息系统&amp;nbsp;&mdash;&amp;nbsp;在新型分布式计算模型、分布式关键技术及其应用及产业化方面开展研究，重点开展基于SaaS和面向服务的金融信息系统软件开发方法；&lt;/p&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;（2）金融数据挖掘&amp;nbsp;&mdash;&amp;nbsp;主要研究大规模数据的分布式存储和检索方法，尤其是面向云计算平台的分布式数据存储，重点开展面向财税金融领域的数据挖掘、分析方法；&lt;/p&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;（3）金融信息安全&amp;nbsp;&mdash;&amp;nbsp;主要研究面向移动商务的物联网技术、网络信息安全、网络优化理论与技术，研究应用于金融领域的网络安全协议，以及网上银行和安全支付等关键技术；&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;（4）网络银行与移动商务&amp;nbsp;&mdash;&amp;nbsp;主要研究金融机构借助网上银行和移动商务平台，将网银与企业财务系统、海关业务系统及电子口岸系统等互联对接，并增加终端数量，扩大技术、设备兼容性等。&lt;/p&gt;', '', '本中心秉承合作共赢、开放发展的理念，以服务社会、满足市场需求为导向，将计算机技术与财税、金融等优势学科充分结合，与各银行、融资机构在以下四个方向进行战略性的合作研究： （1）金融信息系统&nbsp;&mdash;&nbsp;在新型分布式计算模型、分布式关键技术及其应用及产业化方面开展研究，重点开展基于SaaS和面向服务的金融信息系', '', 1, 0, 0, 11, 0, 1, '2012-04-28 08:39:58', '2012-04-14 23:29:45', 0),
(9, 1, 17, '科研成果', '&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;中心注重科研成果的推广转化，独立研究及与合作伙伴共同研发的成果有：&lt;/p&gt;&lt;ul style=&quot;list-style-type:disc;&quot;&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;商业银行资产风险监控系统&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;商业银行押品风险管理系统&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;商业银行授信审批分析及监控系统&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;商业银行贷后监控管理系统&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;商业银行内部评级系统&lt;/p&gt;&lt;/li&gt;&lt;li&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;财务分析及风险预警系统&lt;/p&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;line-height:32px;text-indent:2em;text-align:left;margin:2px 0cm;&quot;&gt;以上科研成果已广泛应用于交通银行、广发银行、恒丰银行、齐鲁银行及小额贷款公司等非银行金融机构。&lt;br /&gt;&lt;/p&gt;', '', '中心注重科研成果的推广转化，独立研究及与合作伙伴共同研发的成果有： 商业银行资产风险监控系统 商业银行押品风险管理系统 商业银行授信审批分析及监控系统 商业银行贷后监控管理系统 商业银行内部评级系统 财务分析及风险预警系统 以上科研成果已广泛应用于交通银行、广发银行、恒丰银行、齐鲁银行及小额贷款公司', '', 1, 0, 0, 11, 0, 1, '2012-04-25 10:03:56', '2012-04-14 23:38:47', 0),
(10, 1, 22, '银监会：继续推进差异化监管政策落地', '&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;中国&lt;span style=&quot;color:#000&quot;&gt;银监会&lt;/span&gt;9日启动&ldquo;&lt;span style=&quot;color:#000&quot;&gt;中国银行&lt;/span&gt;业小微企业金融服务成就展暨宣传月活动&rdquo;。银监会主席&lt;span style=&quot;color:#000&quot;&gt;尚福林&lt;/span&gt;在启动仪式上说，银监会将继续推进差异化监管政策落地，敦促银行业金融机构不折不扣地落实好服务实体经济这一基本要求，更好地服务小微企业。&lt;/p&gt;&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;尚福林说，银行业的稳健发展离不开实体经济这一基础。支持实体经济发展，小微企业是重头戏。银监会下一步将继续推进差异化监管政策的落地；推动小型金融机构建设，扩大小微企业金融服务覆盖面；确保小微企业&lt;span style=&quot;color:#000&quot;&gt;贷款&lt;/span&gt;实现&ldquo;两个不低于&rdquo;（增速不低于全部贷款平均增速、增量不低于上年同期水平）目标。&lt;/p&gt;&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;2008年，银监会提出&ldquo;两个不低于&rdquo;目标，确保对小微企业贷款总量倾斜。2011年，银监会先后出台《关于支持&lt;span style=&quot;color:#000&quot;&gt;商业&lt;/span&gt;银行进一步改进小企业金融服务的通知》及其补充通知，在机构准入、资本占用、存贷比考核、不良贷款容忍度和服务收费等方面，提出了更具体的差别化监管和激励政策，支持银行业金融机构进一步加大对小微企业的&lt;span style=&quot;color:#000&quot;&gt;信贷&lt;/span&gt;支持力度。&lt;/p&gt;&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;为敦促银行业金融机构进一步提升小微企业金融服务水平，从4月9日起，银监会在全国范围内开展&ldquo;中国银行业小微企业金融服务成就展暨宣传月活动&rdquo;，活动主题为&ldquo;促小微，惠民生，强金融，兴百业&rdquo;。&lt;/p&gt;&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;银监会统计显示，截至2011年末，全国小企业贷款余额为10．8万亿元，约占全部贷款余额的20%，连续3年实现&ldquo;两个不低于&rdquo;目标。&lt;/p&gt;&lt;p style=&quot;line-height:24px;text-indent:2em;text-align:left;margin:12px 0cm;&quot;&gt;同时，小企业贷款质量得到提升。截至2011年末，全国小企业不良贷款余额为2107亿元，比年初减少435亿元；不良贷款率为2．02%，比年初下降0．95个百分点，其中单户授信500万元以下小企业贷款不良率为5．14%。&lt;/p&gt;', '', '中国银监会9日启动&ldquo;中国银行业小微企业金融服务成就展暨宣传月活动&rdquo;。银监会主席尚福林在启动仪式上说，银监会将继续推进差异化监管政策落地，敦促银行业金融机构不折不扣地落实好服务实体经济这一基本要求，更好地服务小微企业。 尚福林说，银行业的稳健发展离不开实体经济这一基础。支持实体经济发展，小微企业是重头', '', 1, 0, 0, 0, 1, 1, '2012-04-29 02:46:57', '2012-04-14 23:19:55', 0),
(11, 1, 26, '合作伙伴', '&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;山东舜德数据管理&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;软件工程有限公司&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;山东华软金盾&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;山东省城商联盟&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;北京用友政务软件有限公司&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;加拿大&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;Goitsys&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;山东中孚&lt;/span&gt;&lt;span style=&quot;font-size:19px&quot;&gt;信息产业股份有限公司&lt;/span&gt;&lt;/p&gt;&lt;p&gt;&lt;span style=&quot;font-size:19px&quot;&gt;山东省农信社&lt;/span&gt;&lt;/p&gt;', '', '山东舜德数据管理软件工程有限公司 山东华软金盾 山东省城商联盟 北京用友政务软件有限公司 加拿大Goitsys 山东中孚信息产业股份有限公司 山东省农信社', '', 1, 0, 0, 31, 0, 1, '2012-04-28 08:32:45', '2012-04-14 19:58:32', 0),
(12, 1, 25, '客户财务分析及预警系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;客户财务分析及预警系统用于提高商业银行客户风险识别及预警能力，为银行对客户进行客户风险识别、客户评级提供依据。通过财务报表、关注指标、趋势波动、占比结构、宏观行业（地区、机构）的分析，建立全面的客户财务风险识别及预警体系，对客户财务指标进行静态及动态预警；对客户财务报告的虚假数据进行反欺诈识别预警；建立财务综合风险评估模型，计算风险指数，预测客户财务风险变化趋势；建立现金流预测模型，对客户未来的偿债能力（一期或多期）进行预测分析；对客户在银行账户明细数据统计分析，进行现金流量及流向的监控预警。对银行发掘客户潜在风险、加强风险控制、降低贷款不良率提供强有力的支持。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统包含财务经营分析、客户风险预警、数据分析建模三大部分。系统架构如下：&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334542193946fKLjiG.jpg&quot; style=&quot;width:704px;height:499px;&quot; width=&quot;704&quot; height=&quot;499&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;该系统由山东省金融信息工程技术研究中心金融风险管理研究所与合作伙伴山东舜德数据管理公司共同研究开发，已成功应用于工商银行山东省分行、广发银行、齐鲁银行等金融机构。&lt;/p&gt;', '1334542193946fKLjiG.jpg', '客户财务分析及预警系统用于提高商业银行客户风险识别及预警能力，为银行对客户进行客户风险识别、客户评级提供依据。通过财务报表、关注指标、趋势波动、占比结构、宏观行业（地区、机构）的分析，建立全面的客户财务风险识别及预警体系，对客户财务指标进行静态及动态预警；对客户财务报告的虚假数据进行反欺诈识别预警；', '', 0, 0, 0, 0, 1, 1, '2012-04-15 18:12:03', '2012-04-15 18:11:55', 0),
(13, 1, 25, '金融内网安全与网络行为管理系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;金融内网安全与网络行为管理系统（FISBMS）可实时监测金融机构内网的运行，洞察客户端的一举一动，使得内网安全的诸多隐患得到全面的管理和监控；使得文档更加安全，内网的金融情报和用户数据信息不再担心外泄，几乎封堵了现在能够想到的所有泄密的途径，提供了目前国内最具竞争力的全面内网安全解决方案。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;FISBMS是在采用最新信息安全技术和&ldquo;全面内网安全管理&rdquo;及&ldquo;终端统一威胁管理&rdquo;先进管理理念的基础上推出的创新性的安全产品。它不但全面整合了监控软件、网管软件、加密软件、内网安全等产品的功能，同时吸取Microsoft的网络接入保护(NAP)和Cisco网络准入控制(NA)技术的精髓，创造性地推出软硬件结合的网络接入控制保护系统(NACP)。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334544034000ufEFKZ.jpg&quot; width=&quot;731&quot; height=&quot;322&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;width:731px;height:322px;&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;NACP系统充分发挥了NAC实施简单、效果显著和NAP控制深入、全面防护的优势，在深刻理解用户的需求和行业发展趋势的基础上，有力规避了金融机构内网安全需要大量手动安装客户端软件、客户端易被破坏，用户体验效果差，软件不能体现自身价值等行业诟病，真正为金融用户构建了一套可信赖，易维护、可网管的全面内网安全管控平台，并据此为金融用户提供了在一个可信网络环境下网络使用的规则和制度。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;FISBMS是由山东省金融信息工程技术研究中心金融信息安全研究所与合作伙伴山东华软金盾公司共同研究开发的全新一代软硬结合的可信内网安全管控平台，属国内首创，已成功应用于工商银行、交通银行等国内各个级别的商业银行和其它金融机构。&lt;/p&gt;', '1334544034000ufEFKZ.jpg', '金融内网安全与网络行为管理系统（FISBMS）可实时监测金融机构内网的运行，洞察客户端的一举一动，使得内网安全的诸多隐患得到全面的管理和监控；使得文档更加安全，内网的金融情报和用户数据信息不再担心外泄，几乎封堵了现在能够想到的所有泄密的途径，提供了目前国内最具竞争力的全面内网安全解决方案。 FISB', '', 0, 0, 0, 0, 1, 1, '2012-04-15 18:41:27', '2012-04-15 18:41:23', 0),
(14, 1, 25, '贷后工作监控管理系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统针对贷后工作管理较薄弱、流于形式的现状，提供流程化配置管理，对日常贷后检查监控工作是否及时准确提供监督，对工作内容进行考核统计。是将贷后检查监控工作纳入信息化、自动化、流程化、标准化、可监控、可干预的一体自动工作模式。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;全面的贷后日常业务管理。强化贷后检查监控工作的管理及工作的有效性，包括对预警业务处理、上级检查业务、重大事项报告、贷款资金流向监测、客户资金监测、客户财务综合分析、保证能力检查、抵质押能力检查、督办业务处理、间隔期检查在内的各种贷后监测日常业务进行全面跟踪管理。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;及时的信息转发和全面的信息共享。根据客户的贷款额、融资额、企业规模等不同，对其在贷后监测业务中的异常情况进行不同等级的信息转发，第一时间通知相关各级部门和人员。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;预警信息和贷后检查结果信息汇总形成基于业务和工作的综合分析。对所有的日常业务处理信息，系统提供金字塔形式、多维度、多主线的综合查询分析，包括以客户为主线的所有监测业务查询、以人员为单位向岗位、机构汇总，分析工作是否及时到位，可为银行的贷后工作考核统计提供数据支持。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统架构如下：&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334544136043X7BWsW.jpg&quot; style=&quot;width:638px;height:513px;&quot; width=&quot;638&quot; height=&quot;513&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;该系统由山东省金融信息工程技术研究中心与合作伙伴山东舜德数据公司共同研究开发，已成功应用于广发银行、工商银行山东省分行等金融机构。&lt;/p&gt;', '1334544136043X7BWsW.jpg', '系统针对贷后工作管理较薄弱、流于形式的现状，提供流程化配置管理，对日常贷后检查监控工作是否及时准确提供监督，对工作内容进行考核统计。是将贷后检查监控工作纳入信息化、自动化、流程化、标准化、可监控、可干预的一体自动工作模式。 全面的贷后日常业务管理。强化贷后检查监控工作的管理及工作的有效性，包括对预警', '', 0, 0, 0, 1, 1, 1, '2012-04-27 05:40:27', '2012-04-15 18:43:31', 0),
(15, 1, 25, '商业银行经营分析系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;商业银行在经营管理、决策支持方面面临的数据呈金字塔型。本系统面向商业银行领导以及各级经营决策和经营管理人员，具有对银行经营状况进行常规的查询统计和深入的智能分析等功能。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统分析内容涉及经营管理的方方面面，并从多角度、全方位、深层次分析各种经营指标、风险预警指标。在输出形式上以仪表盘、直方图、饼图、折线图等多种方式展示分析结果。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/13345460456044gwPbX.jpg&quot; style=&quot;width:679px;height:465px;&quot; width=&quot;679&quot; height=&quot;465&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统基于先进的金融数据建模和WEB开发框架，融合数据ETL技术、多维分析技术与FLASH数据展示技术。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334546059770Ff6UMM.jpg&quot; style=&quot;width:651px;height:372px;&quot; width=&quot;651&quot; height=&quot;372&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;该系统由山东省金融信息工程技术研究中心与合作伙伴山东舜德数据管理公司共同研究开发，已成功应用于德州银行、临商银行及其他金融机构。&lt;/p&gt;', '13345460456044gwPbX.jpg', '商业银行在经营管理、决策支持方面面临的数据呈金字塔型。本系统面向商业银行领导以及各级经营决策和经营管理人员，具有对银行经营状况进行常规的查询统计和深入的智能分析等功能。 系统分析内容涉及经营管理的方方面面，并从多角度、全方位、深层次分析各种经营指标、风险预警指标。在输出形式上以仪表盘、直方图、饼图、', '', 0, 0, 0, 0, 1, 1, '2012-04-15 19:28:24', '2012-04-15 19:16:21', 0),
(16, 1, 25, '商业银行风险监控系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;风险监控系统围绕着风险识别，通过建立一系列风险识别工具，搭建风险监控平台，完成风险识别、预警及全过程的风险监控，成为风险管理部门的日常工作平台。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;通过风险监控系统将商业银行目前分散的、欠缺的风险监控、预警等管理工作&ldquo;落地&rdquo;，通过该系统提供一系列的工具、方法和风险管理流程，帮助银行管理人员及时、准确、有效地识别出各项业务存在的风险点和风险预警信号，实现风险关口前移；同时加强风险信息的跟踪处理及监控管理，建立起针对风险预警信号的监测、控制、反馈和化解、责任认定和追究等各环节组成的闭环处理机制，变单向风险监控预警管理为立体式监控预警管理。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统功能架构：&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334546314176lFjEhs.jpg&quot; style=&quot;width:649px;height:377px;&quot; width=&quot;649&quot; height=&quot;377&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;系统逻辑架构：&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334546332132LWaKHC.jpg&quot; style=&quot;width:685px;height:386px;&quot; width=&quot;685&quot; height=&quot;386&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;该系统由山东省金融信息工程技术研究中心金融风险管理研究所与合作伙伴山东舜德数据管理公司共同研究开发，已成功应用于交通银行、广发银行、恒丰银行、齐鲁银行、德州银行、济宁农信（省农信试点单位）。&lt;/p&gt;', '1334546314176lFjEhs.jpg', '风险监控系统围绕着风险识别，通过建立一系列风险识别工具，搭建风险监控平台，完成风险识别、预警及全过程的风险监控，成为风险管理部门的日常工作平台。 通过风险监控系统将商业银行目前分散的、欠缺的风险监控、预警等管理工作&ldquo;落地&rdquo;，通过该系统提供一系列的工具、方法和风险管理流程，帮助银行管理人员及时、准确、', '', 0, 0, 0, 0, 1, 1, '2012-04-15 19:28:23', '2012-04-15 19:26:04', 0),
(17, 1, 25, '客户信用风险评级系统', '&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;客户信用风险评级系统基于Basel&amp;nbsp;II标准框架，以风险数据分析、客户评级模型为基础，为商业银行、担保公司等金融机构的授信决策提供科学依据和决策支持。系统涵盖从数据分析、评级模型建立、模型回测验证、软件实现到应用的全面的评级体系。系统支持传统的打分卡客户评级模型，也支持根据历史数据统计分析的量化评级模型。对于历史违约样本数据不足的客户群，可采取定量加定性的传统打分卡评级模式，对于历史违约样本数据充足的客户群，可采用基于统计的量化评级模型进行评级。&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;评级对象包括商业银行、担保公司等金融机构的法人客户、小企业客户、个人客户等。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/13345468342229V4Fjj.jpg&quot; style=&quot;width:662px;height:463px;&quot; width=&quot;662&quot; height=&quot;463&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;客户评级模型开发建模&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334546813742XUHiUG.jpg&quot; style=&quot;width:699px;height:449px;&quot; width=&quot;699&quot; height=&quot;449&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;line-height:28px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;&quot;&gt;该系统由山东省金融信息工程技术研究中心金融风险管理研究所、信用保险研究所与合作伙伴山东舜德数据管理公司共同研究开发，已成功应用于广发银行、恒丰银行、东莞银行及其它金融机构。&lt;/p&gt;', '13345468342229V4Fjj.jpg', '客户信用风险评级系统基于Basel&nbsp;II标准框架，以风险数据分析、客户评级模型为基础，为商业银行、担保公司等金融机构的授信决策提供科学依据和决策支持。系统涵盖从数据分析、评级模型建立、模型回测验证、软件实现到应用的全面的评级体系。系统支持传统的打分卡客户评级模型，也支持根据历史数据统计分析的量化评级', '', 0, 0, 0, 0, 1, 1, '2012-04-15 19:28:22', '2012-04-15 19:28:20', 0),
(18, 1, 3, '山东金融信息工程技术研究中心网站正式启用', '&lt;p&gt;山东省金融信息工程技术研究中心网站正式启用&lt;img src=&quot;http://img.baidu.com/hi/jx2/j_0002.gif&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; /&gt;&lt;br /&gt;&lt;/p&gt;', '1334474546620.png', '山东省金融信息工程技术研究中心网站正式启用', '', 0, 0, 0, 0, 1, 1, '2012-04-12 23:14:32', '2012-04-12 23:14:32', 0),
(19, 1, 3, '山东省金融信息工程技术研究中心学术骨干座谈会顺利召开', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;为进一步推进学术骨干申请国家自然科学基金的申请工作，提升金融信息工程技术研究中心团队的凝聚力，加强计算机专业骨干与金融专业骨干的沟通协调，2月17日（周五），由中心主任徐如志主持的学术骨干座谈会暨2012国家自然基金项目申报交流会顺利召开。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:center&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334987813620-J7B19s.JPG&quot; width=&quot;600&quot; height=&quot;397&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;text-indent:2em;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;徐如志教授，优秀的青年教师杨峰、聂秀山分别从时局政策、评委组织工作、组建课题小组、申请书撰写等方面向与会的二十多位学术骨干介绍了申请国家自然基金项目的经验。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;金融学院的安起光教授就加强计算机和金融学科之间、学术骨干之间的信息交流和项目合作的问题上提出了诸多建设性的意见，并得到大家的认同。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;科技处的马玉林也对本中心的多次工作给予了肯定，并希望全体与会人员再接再励，争取在2012年获得更多高水平的国家自然基金项目立项。\r\n&lt;/p&gt;', '1334987813620-J7B19s.JPG', '&nbsp;&nbsp;&nbsp;&nbsp;为进一步推进学术骨干申请国家自然科学基金的申请工作，提升金融信息工程技术研究中心团队的凝聚力，加强计算机专业骨干与金融专业骨干的沟通协调，2月17日（周五），由中心主任徐如志主持的学术骨干座谈会暨2012国家自然基金项目申报交流会顺利召开。 &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;徐如志教授，优秀的青年教师杨峰、', '', 0, 0, 0, 3, 1, 1, '2012-04-28 03:38:14', '2012-04-20 22:05:29', 0),
(20, 1, 3, '山东省金融办领导与专家来本中心座谈交流', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;2012年2月16日下午，山东省金融办综合处王广庆处长、银行处柏涛副处长一行莅临本中心参观指导工作。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-align:center&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334988471768-7JYS5h.JPG&quot; width=&quot;600&quot; height=&quot;397&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;text-indent:2em;&quot; /&gt;\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;中心主任徐如志就新山东财经大学的学科特色，中心的成立背景，目标定位和服务对象，近期开展的工作以及未来的工作方向等问题逐一向金融办的领导进行了汇报。\r\n&lt;/p&gt;\r\n&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;\r\n&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;王广庆处长对本中心一年多来所做的努力给予了充分的肯定，并对本中心未来的工作方向提出了中肯的、专业的建议。他指出，本中心将计算机和山财大优势学科金融、财税相结合，与本地企业合作，为中小金融机构服务，发展潜力巨大，前景广阔。他还鼓励本中心的工作人员积极了解时局政策，努力将金融信息工程中心打造成一个&ldquo;产学研&rdquo;相结合的优质平台，为社会创造出更多的计算机、金融双料人才。\r\n&lt;/p&gt;', '1334988471768-7JYS5h.JPG', '&nbsp;&nbsp;&nbsp;&nbsp;2012年2月16日下午，山东省金融办综合处王广庆处长、银行处柏涛副处长一行莅临本中心参观指导工作。 &nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;中心主任徐如志就新山东财经大学的学科特色，中心的成立背景，目标定位和服务对象，近期开展的工作以及未来的工作方向等问题逐一向金融办的领导进行了汇报。 &nbsp;&nbsp;&nbsp;&nbsp;王广庆处长对本中', '', 0, 0, 0, 17, 1, 1, '2012-04-28 07:33:21', '2012-04-20 22:31:35', 0),
(21, 1, 4, '四部委联手助力中小企业', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;四部委联手助力中小企业，涉及税收优惠、债券股票融资、创投基金、服务平台、鼓励出口等多个方面。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;为切实改善中小企业融资环境，更好地服务实体经济，包括国家发改委、财政部、工信部和商务部在内的多个部委密集出台了一系列针对中小企业的扶持政策。从税收、财政、金融以及在市场准入等方面解决各项难题，进一步促进中小企业的健康发展。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;财政部：税收优惠财政扶持&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;在今年的全国财政工作会议上，财政部部长谢旭人表示，2012年要大力支持中小企业发展，落实好小微企业的企业所得税优惠以及提高增值税、营业税起征点等税费减免政策，推动产业结构优化升级。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;税收优惠的具体措施包括，自2011年11月1日起，大幅提高增值税和营业税起征点；自2012年1月1日至2015年12月31日，将小型微型企业减半征收企业所得税政策，延长执行期限并扩大范围；2011年11月1日至2014年10月31日，免征金融机构与小型微型企业签订的借款合同印花税；延长金融企业中小企业贷款损失准备金税前扣除政策；对符合条件的国家中小企业公共服务示范平台中的技术类服务平台纳入现行科技开发用品进口税收优惠政策范围等等。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong style=&quot;text-indent:2em;&quot;&gt;发改委：引导民资发展方向&lt;/strong&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;去年，国家发展改革委公布《关于鼓励和引导民营企业发展战略性新兴产业的实施意见》使民营资本投资战略性新兴产业得到实质性落实，民营企业迎来了更大发展空间。在资金保障方面，意见要求，引导民资设立创业投资和产业投资基金，支持民企充分利用新型金融工具融资。积极支持和帮助符合条件的民营企业发行债券、上市融资、开展新型贷款抵押和担保方式试点等，改进对民营企业投资战略性新兴产业相关项目的融资服务。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;截至2011年底，全国共有24个省市设立了战略性新兴产业专项资金。近日，发改委新批复了41只创投基金的设立方案，吸引社会资本70多亿元。发改委财政金融司处长刘健钧表示，创投基金是中小企业的发动机。没有它，中小企业&ldquo;巧妇难为无米之炊&rdquo;。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong style=&quot;text-indent:2em;&quot;&gt;工信部：六项服务营造氛围&lt;/strong&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;4月20日，工业和信息化部中小企业司司长郑昕表示，根据当前中小企业存在的突出问题，工信部把2012年定为&ldquo;中小企业服务年&rdquo;。主旨是&ldquo;服务企业、助力成长&rdquo;，要围绕着今年所定的&ldquo;稳中求进、科学发展&rdquo;的主线全面地为企业开展&ldquo;三送&rdquo;服务，&ldquo;送政策&rdquo;、&ldquo;送服务&rdquo;、&ldquo;送温暖&rdquo;。一共开展了六项服务，包括：政策咨询服务；还有针对中小企业存在的若干问题提供的融资服务；创业创新服务；转型升级服务；管理提升服务以及舆论服务，要在全社会为中小企业发展营造一个良好的氛围。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;据悉，工信部将编制出台中小企业服务体系建设十二五规划，出台《关于加快推进中小企业服务体系建设的指导意见》，并会同有关部门研究制定支持中小企业公共服务平台发展的税收优惠政策。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong style=&quot;text-indent:2em;&quot;&gt;商务部：鼓励&ldquo;出海闯关&rdquo;&lt;/strong&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;text-indent:2em;&quot;&gt;金融危机和欧债危机之后，全球需求端极度萎缩，各类保护主义风潮渐起以及新一轮全球产业洗牌渐趋形成，很多中小企业都需要寻找新的突破，到海外寻找商机。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;近期，商务部表示，将鼓励在资本总量、人才结构等方面有条件的中小企业&ldquo;走出去&rdquo;，并给予全面周到的法律咨询服务和所在国国情介绍，以控制风险。与此同时，国家支持中小企业&ldquo;走出去&rdquo;的实施细则也将很快出台。中小企业如何下好&ldquo;走出去&rdquo;这盘棋无论是对于其自身，还是中国整体战略转型，都具有重大的战略意义。&lt;/p&gt;', '', '四部委联手助力中小企业，涉及税收优惠、债券股票融资、创投基金、服务平台、鼓励出口等多个方面。 为切实改善中小企业融资环境，更好地服务实体经济，包括国家发改委、财政部、工信部和商务部在内的多个部委密集出台了一系列针对中小企业的扶持政策。从税收、财政、金融以及在市场准入等方面解决各项难题，进一步促进中小', '', 0, 0, 0, 8, 1, 1, '2012-04-20 23:18:45', '2012-04-20 22:34:51', 0),
(22, 1, 4, '信息化金融服务破解中小企业融资难题', '&lt;p style=&quot;line-height:23px;text-indent:2em;text-align:left;margin-top:auto;margin-bottom:auto;&quot;&gt;交通银行金融研究中心预计，今年在货币政策总体适度放松的大环境下，社会融资总规模将扩大到13万亿元至14万亿元，如果考虑到资金流转率，这个数字将达到百万亿元级。然而，大量中小企业的融资困境并未获得明显改善。&lt;/p&gt;&lt;p style=&quot;line-height:23px;text-indent:2em;text-align:left;margin-top:auto;margin-bottom:auto;&quot;&gt;专家指出，基于传统的融资模式，当前很多发展势头良好的中小企业往往受困于融资渠道问题而裹足不前，丧失了进一步扩大规模的机会。在这种情况下，寻求多元化融资渠道，探索创新的融资模式，成为企业的必然选择，这为创新型金融服务企业带来了市场机会。&lt;/p&gt;&lt;p style=&quot;line-height:23px;text-indent:2em;text-align:left;margin-top:auto;margin-bottom:auto;&quot;&gt;近日，快钱公司启用全新品牌形象，成为首家为企业提供信息化金融服务的支付企业，它致力于帮助企业快速获取和优化现金流。快钱CEO关国光说，&ldquo;信息化金融时代的到来，使得以信息技术优化流动资金管理成为可能。我们依托业已建立的信息化支付清算平台，持续打造包括电子收付款、应收应付账款融资等创新产品组合，形成一套流动资金管理解决方案，解决中小企业融资难题，助力中小企业快速获取及优化现金流，提升企业流动资金管理效率。&rdquo;&lt;/p&gt;&lt;p style=&quot;line-height:23px;text-indent:2em;text-align:left;margin-top:auto;margin-bottom:auto;&quot;&gt;当前企业面临的资金难题表现在两个方面，一是资金归集速度慢，尤其是在&lt;a href=&quot;http://youhui.163.com/&quot; title=&quot;连锁&quot;&gt;&lt;span style=&quot;color:#000&quot;&gt;连锁&lt;/span&gt;&lt;/a&gt;、零售等传统行业里较为明显；二是应收账款无法及时回收，账期拉长影响企业扩张速度。此外，很多企业的信息流和资金流是手工对账，财务管理效率低下，拉高了企业经营成本。一些创新型金融服务企业利用信息化支付清算平台，推出包括电子收付款、应收应付账款融资等创新产品组合，形成一套流动资金管理解决方案。&lt;/p&gt;', '', '交通银行金融研究中心预计，今年在货币政策总体适度放松的大环境下，社会融资总规模将扩大到13万亿元至14万亿元，如果考虑到资金流转率，这个数字将达到百万亿元级。然而，大量中小企业的融资困境并未获得明显改善。 专家指出，基于传统的融资模式，当前很多发展势头良好的中小企业往往受困于融资渠道问题而裹足不前，', '', 0, 0, 0, 0, 1, 1, '2012-04-20 23:18:50', '2012-04-20 23:15:17', 0),
(23, 1, 4, '考虑放宽中小金融机构准入门槛', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;随着温州金融综合改革试验区的成立，业内对我国金融改革的关注日渐升温。温州金融改革有怎样的背景？我国需要发展什么样的金融服务体系？如何引导民间资本进入金融领域？记者就此采访了中国人民银行研究局局长张健华。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;构建基层金融服务体系&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;记者：温州金融综合改革试验区日前获国务院批准，创建温州金融综合改革试验区的背景和意义是什么？&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;张健华：温州具有民营经济发达、民间资金充裕、民间金融活跃的优势。但与此同时，温州又存在&ldquo;两多、两难&rdquo;问题，即民间资金多但投资难，中小企业多但融资难。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;放宽准入，推动形成有效竞争的基层金融服务体系，是破解温州&ldquo;两多、两难&rdquo;问题的重要方向。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;在温州开展金融综合改革试验，应结合温州的特点，在加强监管、有效防范风险的前提下，放宽中小金融机构的市场准入，构建竞争性的基层金融服务体系，有效破解&ldquo;两多、两难&rdquo;问题，进一步提升金融服务实体经济的能力，推动温州经济转型升级。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;加快发展社区型金融机构&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;记者：近年来我国银行业发展迅猛，但与大型金融机构相比，我国基层金融服务似有不足，未来应如何发展？&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;张健华：经过多年改革发展，我国金融服务于实体经济的能力得到了很大改善，但基层(主要是县域)金融服务竞争不足、服务能力偏弱。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;从国内金融改革和发展趋势看，前5家大型银行在银行业存款类金融机构存款市场中所占的份额从2002年的约80%降为2011年的约50%，贷款份额则从约80%降到不足50%，垄断现象并不明显。但必须指出，在中小城市社区和广大农村地区，金融服务虽有很大改善，竞争依然不足，存在高层说的垄断现象。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;我国不仅地域和经济总量大，又存在巨大的地区差异和城乡差别，要想实现基层金融服务的充分竞争，只靠大中机构恐怕不够，要考虑发展主要服务小微企业和当地居民的社区型金融机构。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;放宽中小金融机构准入门槛&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;记者：近期以来，市场关于放宽金融机构准入门槛的呼声越来越高，如何看待市场的这一呼声？&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;张健华：金融作为现代服务业，是国民经济的命脉，其特殊性在于风险相对较大，但本质上仍属于竞争性行业，都是在竞争格局中向社会提供商品和服务。竞争最终会使供求基本平衡，多数供给和需求得到满足，还会降低价格，减少企业欺压客户的可能性，压缩寻租空间。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;从金融消费者保护的角度看，其重要目标是基层的消费者拥有更多的、和大中城市消费者平等的选择权。这些基本权利包括：消费者能够在存款和其他形式产品之间，境内产品和境外产品之间，以及不同金融机构之间进行选择。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;无论是提供产品还是服务，市场经济的一项基本规律就是准入自由，从而形成并动态维持竞争格局。应考虑放宽金融业特别是社区型中小金融机构的准入，以构建有效竞争的格局。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;打破垄断引导民资进入&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;记者：国务院支持民间投资健康发展的新36条明确规定，允许民间资本兴办金融机构。我们应怎样引导民间资本进入金融领域？&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;张健华：我国目前各项法规对于民间资本进入金融服务领域并无任何形式上的限制，但在实践中，即使是小型金融机构，民间资本也很难直接发起设立。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;金融业外部效应大，准入门槛高，但与市场经济的自由准入原则并不相悖。从国际经验看，金融机构设立时，很少有国家按股东性质限制准入。一般而言，只要有符合监管要求的足够资本、拥有合格的经营管理人才、具备相应风险控制制度及管理信息系统，就应该允许设立。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;我国目前要求大型金融机构设立小机构来扩大基层金融服务的初衷虽好，但并不一定符合市场化的改革方向。大银行如真心想扩大基层业务，直接设立分支机构比投资办独立的村镇银行更具经济合理性。让大银行作为发起股东履行对村镇银行的日常监管责任并承担村镇银行经营失败时的风险处置责任，亦超出了公司法规定的股东责任范围。&lt;/p&gt;', '', '随着温州金融综合改革试验区的成立，业内对我国金融改革的关注日渐升温。温州金融改革有怎样的背景？我国需要发展什么样的金融服务体系？如何引导民间资本进入金融领域？记者就此采访了中国人民银行研究局局长张健华。 构建基层金融服务体系 记者：温州金融综合改革试验区日前获国务院批准，创建温州金融综合改革试验区的', '', 0, 0, 0, 0, 1, 1, '2012-04-20 23:18:52', '2012-04-20 23:16:35', 0),
(24, 1, 4, '第三方支付助推中小银行基金销售', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;尽管近年来基金市场乏善可陈，销售陷入困顿，但通过第三方机构支付实现的基金交易量却取得了飞速增长。&ldquo;基金&rdquo;这块大蛋糕成了第三方支付企业垂涎的对象。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;近日，由通联支付网络股份有限公司主办的、以&ldquo;通金融服务、联银行资源&rdquo;为主题的基金销售与理财服务高层研讨会在厦门召开。会上，第三方支付企业如何帮助中小银行开展基金销售业务并提高整体理财服务能力，如何提升中小银行在基金销售中的占比，成为业内专家讨论的热点。中小银行看好基金销售&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;数据显示，中小银行的基金销售收入占比不足全国性银行的1%，但全国有代销资质的银行中，中小银行在数量上占了71%，数量上的优势还来不及转化为收入上的优势，对中小银行来说，基金销售业务未来的市场发展空间巨大。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;同时，基金销售业务作为银行的一项重要中间业务，对于中小银行而言不仅是收入的重要来源，也会带来多方面的效益。比如这项业务将为客户提供更多理财服务、可增加现有客户的忠诚度以及有利于拓展新客户、提升市场竞争力等。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;其实，不光是巨大的市场容量为中小银行开展基金销售业务提供了一个广阔的发展平台，客户日渐增长的投资需求以及银行长期的战略发展定位，都要求中小银行大力发展基金销售业务。但由于中小银行在基金销售方面起步较晚，因此在业务开展上还需要基金公司、第三方支付机构等各方的支持与协助。第三方支付企业将大有作为&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;面对中小银行的这种切实需求，目前获得基金支付业务牌照的第三方支付企业将大有作为。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;通联支付金融服务事业部副总裁周盛在会上向邀请嘉宾以及参会银行解析了通联支付在基金销售业务中的角色和定位以及为中小银行提供的服务价值。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;周盛表示，具有基金支付资质的第三方支付企业参与基金销售业务，能有效地降低各环节相应的成本，比如节约了投资者购买基金的时间成本、利用现有平台减少中小银行与基金公司的链接成本；同时通过平台可以实现将各银行的接口统一链接，从而可以扩大银行卡支付的范围，解决中小银行的业务区域局限。此外，第三方支付企业的参与优化了基金销售行业的专业化分工，有利于行业创新业务的发展。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;基金支付成为下一个&ldquo;蓝海&rdquo;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;业内人士指出，基金支付业务或许将成为第三方支付行业的下一个蓝海。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;据了解，目前国内获准开展基金支付业务的第三方支付企业仅有四家，而通联支付是第二家获得该牌照的支付企业。该公司成立于2008年10月，是目前国内注册资本最为雄厚的第三方支付企业。2011年5月，该公司成为第一批获得第三方支付牌照的企业之一，目前也是支付机构中惟一一家当选中国支付清算协会副会长级会员的单位。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;如今，基金支付业务这片蓝海已经打开，获准开展基金支付业务的四家第三方支付企业正&ldquo;摩拳擦掌&rdquo;，纷纷进军这一市场。谁能抢得先机、谁能最准确地把握市场的需求，谁能找到最合适的经营模式，谁就能在未来的市场竞争中抢得先机。&lt;/p&gt;', '', '尽管近年来基金市场乏善可陈，销售陷入困顿，但通过第三方机构支付实现的基金交易量却取得了飞速增长。&ldquo;基金&rdquo;这块大蛋糕成了第三方支付企业垂涎的对象。 近日，由通联支付网络股份有限公司主办的、以&ldquo;通金融服务、联银行资源&rdquo;为主题的基金销售与理财服务高层研讨会在厦门召开。会上，第三方支付企业如何帮助中小银行开', '', 0, 0, 0, 1, 1, 1, '2012-04-28 03:36:21', '2012-04-20 23:19:55', 0),
(25, 1, 3, '校长刘兴云到计算科学与技术学院进行调研', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;4月13日上午，校长刘兴云到计算机科学与技术学院（以下简称计算机学院）进行工作调研,&amp;nbsp;校办主任张书学陪同调研。听取计算机学院领导班子的工作汇报，了解学校合并后计算机学院工作开展情况、存在的问题和今后学院的工作思路。&lt;/p&gt;&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/CMS/static/uploads/gallery/gallery-big/1334996709146-INhhrZ.jpg&quot; width=&quot;600&quot; height=&quot;400&quot; border=&quot;0&quot; hspace=&quot;0&quot; vspace=&quot;0&quot; style=&quot;text-align:center;text-indent:2em;&quot; /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;徐如志院长汇报了计算机学院围绕学校的总体部署抓稳定，坚持稳中求进，确保教学和学生管理工作有序进行；抓班子，落实党政联席会议制度，坚持集体决策和分工负责；抓基础，以人才培养和学科建设为核心，积极推进学院各项工作。张彩明院长汇报了数字媒体重点实验室建设存在的问题和数字媒体专业建设规划以及对人才评价体系的建议。孙青书记汇报了计算机学院在思想建设、和谐院系建设方面开展的工作，介绍了党总支在学院工作中如何保驾护航，讲政治、顾大局，积极推进计算机学院师生在思想认识和行动上的快速融合。学院其他领导也就各自分管工作提出意见和建议。&lt;br /&gt;&lt;/p&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;刘校长介绍了最近学校在去筹和部省共建方面取得的进展，认真听取了计算机学院各位领导的发言，对计算机学院合校后的工作给予了充分肯定。对计算机学院领导班子当前的工作状态以及稳中求进的工作态度表示欣慰。对计算机学院的工作提出了五点要求。&lt;/div&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;一是理清思路，特色发展。计算机学院要立足学校的财经学科优势，找准学科发展定位和学科发展方向，积极服务财经、服务社会，为我校的财经学科提供有力支撑，在此基础上办出特色办出水平。&lt;/div&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;二是抓住龙头，突出重点。要充分发挥计算机学院在科研和师资队伍方面的优势，以学科建设为龙头，进一步凝练学科方向，突出重点，为博士点建设早作准备，强调要抓好学科专业建设和人才储备工作。&lt;/div&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;三是教学为重，改革方法。教学工作是学校的中心工作，教师要把主要精力放在教学上。计算机学院除了本专业的课程外，还承担全校的计算机基础课教学，对全校的教学质量和人才培养质量有重要影响。要采取措施、鼓励教师搞好教学，同时要利用信息技术改革教学方法，争取在教改方面走在前列。&lt;/div&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;四是注重科研，搞好服务。计算机学科在加强理论研究的同时，积极开展社会服务和应用研究，数字媒体专业要加强与文化创意产业的结合，寻求政府和社会支持；计算机科学与技术专业要重点面向财经领域为财经信息化提供服务。&lt;/div&gt;&lt;div style=&quot;text-indent:2em;text-align:left;&quot;&gt;五是加强团结，求同存异。计算机学院强强合并后领导班子成员都能以事业为重，对工作中出现的意见分歧和认识上的差异能够坦诚布公，求同存异，稳中求进，这是难能可贵的。希望计算机学院领导班子进一步加强沟通、加强团结，带领计算机学院在学科建设和社会服务方面取得更大突破。&lt;/div&gt;', '1334996709146-INhhrZ.jpg', '4月13日上午，校长刘兴云到计算机科学与技术学院（以下简称计算机学院）进行工作调研,&nbsp;校办主任张书学陪同调研。听取计算机学院领导班子的工作汇报，了解学校合并后计算机学院工作开展情况、存在的问题和今后学院的工作思路。 徐如志院长汇报了计算机学院围绕学校的总体部署抓稳定，坚持稳中求进，确保教学和学生管理', '', 0, 0, 0, 30, 1, 1, '2012-04-28 11:31:56', '2012-04-21 00:30:01', 0),
(26, 1, 4, '中行结构性存款大增背后：或为变相高息揽储', '&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;在结构性存款规模暴涨面前，银行三季报显露的存款&ldquo;失血症&rdquo;或许并没有想象中那么严重。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span id=&quot;stock_sh601988&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;中国银行&lt;/span&gt;&lt;span id=&quot;quote_sh601988&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;2.98&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.01&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.33%&lt;/span&gt;)&lt;/span&gt;三季报显示，在三季度客户存款相比二季度负增长1255.95亿元的同时，该行(集团口径，下同)的交易性金融负债(以结构性存款为主)相比二季度却大增了1765.37亿元，超过了存款负增长，而今年以来该行交易性金融负债的规模相比年初增长达到183.77%。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;结构性存款虽属银行理财，但与一般理财不同，其规模也纳入存贷比考核，只是利息支付与普通存款不同，是今年以来银行吸收存款的重要途径。&rdquo;一家银行资金业务相关负责人近日透露，不过今年以来该业务也有问题，不少产品是&ldquo;伪结构性存款&rdquo;，结构性存款成为变相高息揽存的工具。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;结构性存款&ldquo;救场&rdquo;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;在通胀和民间借贷多重因素影响下，商业银行今年负债管理压力普遍增加，大型银行尤其如此，三季报显示，建行、中行的吸收存款负增长都超过了1000亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;另一方面，结构性存款却规模大增。仍以中行半年报为例，结构性存款半年净增2379.73亿元，超过上半年新增客户存款5572.78亿元规模的50%，三季度继续高速增长。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;结构性存款是运用利率、汇率等衍生金融工具与传统存款相结合的产品，虽是存款，但在银行会计核算过程中，一般是列入负债统计里面的交易性金融负债科目，而非吸收存款。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;中行财报显示，在该行交易性金融负债细分结构中，主要包括两大类负债，一是外币证券卖空类的负债，另外就是结构性存款，不过该行卖空类负债的规模和新增很小，比如2011年半年报在4360亿元交易性负债中，只有37亿元负债属于卖空类，上半年新增也仅有15亿元左右。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;在三季报解释交易性金融负债规模大增原因时，该行也表示原因是结构性存款增加。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;中行一家分行的零售业务负责人也坦承，由于结构性存款利息一般比普通存款要高，是今年该行拓展存款的主要工具。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;上市公司&lt;span id=&quot;stock_sz002473&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;圣莱达&lt;/span&gt;&lt;span id=&quot;quote_sz002473&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;11.79&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.26&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;2.25%&lt;/span&gt;)&lt;/span&gt;10月31日公告也显示，该公司今年总共动用募集资金4740万元累计8次购买中行宁波市江北支行的结构性存款，截至2011年6月13日全部赎回所购买的结构性存款产品为止，共获得利息收入198163.30元，比同等存款金额活期利息多约17万元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;记者了解到，今年一些银行的结构性存款理财产品，年化收益一般可以高达5%及以上，相比存款的法定利息，对投资者确实更有吸引力，而且按照央行相关计算贷存比的规定，结构性存款规模是纳入存款统计，无疑能极大释放银行的贷存比考核及流动性压力。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;算上结构性存款的规模，今年以来银行的资金来源压力就没有那么大了，不过结构性存款对银行而言意味着更高的利息成本，对息差和利润是有负面影响的，不是每家银行都会采取增加结构性存款的策略。&rdquo;前述资金业务负责人认为。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;这点在中行身上亦有反映，中行三季度的付息负债成本由二季度的2.18%提高到2.24%，净息差则环比略降了1个基点，第三季度实现拨备前利润323.12&amp;nbsp;亿元，较二季度环比下降22.18%，实现净利润171.63&amp;nbsp;亿元，环比下降15.93%，业绩表现低于此前多家机构的预期。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;存贷联动差异&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;不过，增加结构性存款还不仅只是成本、利润方面的压力。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;前述资金业务负责人介绍，如果是真结构性存款，高于普通存款的利息是合理的，但目前行业现状是大量所谓结构性存款，实质上仍是固定收益类理财产品挂上了结构性存款的外衣，这就涉及到违规高息揽存。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;据了解，结构性存款一个重要的特征，一般有一个收益挂钩模型，比如当汇率走势在某一预定区间波动时，可以获得超额收益等，但很多伪结构性产品都没有类似设计。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;此前社科院金融所理财产品中心还专门就某月理财市场发过一项报告指出，当月市场上的40款结构性产品，只有1款是真结构性理财，其余都是伪结构产品，一度引发舆论哗然。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;各地银监局今年在部分地区对结构性存款也有一些监管，不过还没有全面清查。&rdquo;该负责人透露。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;值得注意的是，同是面对吸收存款负增长，建行并没有像中行那样大量发行结构性存款，该行财报显示，其交易性金融负债主要是与贵金属相关的负债，结构性金融工具规模较小，而且结构性工具上半年几乎是零增长。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;而且在三季度的贷款策略上，两家银行的表现也差异明显。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;建行(微博)前三季度新增贷款6725.09亿元，其中第一季度和第二季度分别新增了2409.70亿元和1312.89亿元，第三季度虽然吸收存款负增长超过1500亿元，但贷款新增明显加速达到3002.5亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;中行第三季度则是减速明显，该行2011年前两季新增贷款分别为2960.75亿元、2598.28亿元，第三季度则仅新增贷款318亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;两家的业绩结果也因此表现迥异，相比中行的利润压力，建行三季度贷款大跃进使得该行成为四大行中唯一三季度不良贷款余额上升的银行，环比上升了12.25亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;中行南京管理部副总经理吕俊峰告诉记者，传统银行当前同时面临客户资产多元化和贷款资源紧缺等形势，该分行四季度将多渠道结合信用卡、网上结算、票据融资等新方式，通过提高产品服务降低存款流失压力。&lt;/p&gt;&lt;div style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin:0px;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;消息来源：http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;2011年11月02日&amp;nbsp;08:10&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;21世纪经济报道&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;微博&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent:2em;color:#A5A5A5;&quot;&gt;徐永&lt;/span&gt;&lt;/div&gt;', '', '在结构性存款规模暴涨面前，银行三季报显露的存款&ldquo;失血症&rdquo;或许并没有想象中那么严重。 中国银行(2.98,-0.01,-0.33%)三季报显示，在三季度客户存款相比二季度负增长1255.95亿元的同时，该行(集团口径，下同)的交易性金融负债(以结构性存款为主)相比二季度却大增了1765.37亿元，超过', '', 0, 0, 0, 1, 1, 1, '2012-04-28 03:02:51', '2012-04-21 06:02:22', 0),
(27, 1, 4, '江西曝高息揽存诈骗案 涉上亿资金借款人已跑路', '&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;一夜之间，&ldquo;占军辉卷款逃跑&rdquo;的消息让江西崇仁这个小县城炸开了锅，对航埠镇的占家村村民来说，不亚于8级地震。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占军辉长年在东莞打工，就是这个初中没毕业的28岁年轻人，在半年时间内，向占家村及航埠镇村民吸收存款数千万元，再加上崇仁县及在外务工人的存款，总额过亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;10月中旬后，占军辉便&ldquo;人间蒸发&rdquo;，这让通过借高利贷、用房产抵押从银行贷款、变卖猪牛等途径筹钱存款到其名下的村民们几近绝望，这些试图通过&ldquo;高息生钱&rdquo;途径发家致富的村民，称自己的损失及债务在有生之年都难以偿还。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;如今，全国信贷紧张、银根紧缩，温州民间借贷崩盘现象频发，不少民间筹资向内地转移，民间借贷在内地城市有愈演愈烈之风。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;11月9日，江西省社科院经济研究所所长麻智辉在接受&amp;nbsp;《每日经济新闻》记者采访时表示，目前全国大部分地区中小企业融资困难，这给民间资本提供了市场。如何正确引导民间资本进入市场，并向公众宣传非法高息融资的风险，以防民间借贷崩盘事件发生，是当前的一个严峻挑战。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;小城&ldquo;地震&rdquo;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占军辉消失的消息，在崇仁县引发了一场地震，震源就在航埠镇占坊村。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;最早发现占军辉消失的是航埠镇的陈小强(化名)，他于10月9日通过银行给占军辉汇款17.8万元，也是最后一批给占军辉汇钱的人。次日，陈小强便听到占军辉要崩盘的风声，于是他打电话给对方要求退钱，占称第二天(10月11日)给他回款，最迟不超过10月12日。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;陈小强告诉《每日经济新闻》记者，10月11日他没有等来汇款，有点坐不住了，拨打占军辉的电话，已经打不通了。此后，占军辉两度变换手机号码联系他，承诺在两日内还钱。但是，陈小强等人一分钱都没等到。占军辉彻底消失了。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;10月19日，陈小强到崇仁县公安局报案。那天，报案的有上百人，&ldquo;简直要把小小的公安局挤爆了，听说笔录都做了3天&rdquo;。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;报案的人，大部分是航埠镇占坊村及邻村的人。报案记录后面，是一串长长的名单：宁财保投入1180万元，占福良投入314万元，黎友生投入400万元，占小明投入63万元，邹团祥投入30万元&hellip;&hellip;所有借钱给占军辉的人，都没有打借条。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;陈小强说，他最后一次见占军辉时，对方告诉他，占坊村投入了3700万元，还有周边村子及崇仁县其他地方的存款。&ldquo;占坊村的投钱户最多。不过数额较大的都来自外村，总额超过亿元。&rdquo;陈小强说。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;航埠镇占坊村的人聚在一起，无不痛恨咒骂占军辉。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;&ldquo;摇钱树&rdquo;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;就在两个月前，村民们对占军辉还像财神一样膜拜。中秋节当天，占军辉从东莞回到占坊村，全村人从几公里外的进村道路上，就开始燃放鞭炮和烟花。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;他开着豪车，身上穿的都是名牌，一块手表都是十多万元。&rdquo;村民占文华对《每日经济新闻》记者说。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;不仅占军辉，他手下的一批同村人也个个派头十足。&ldquo;他们开着几十万元的车，每次回来都提一大袋钱，说这些都是在东莞放高利贷和做生意赚的钱。&rdquo;占文华说，这让同村人羡慕不已。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占军辉初中没毕业就出去打工，10多年来，村里人对他在外面做什么，知之甚少。但其光鲜的外表和手下人的春风得意，让全村人放松了戒备，甚至忘记了他在东莞曾因敲诈坐过四年牢的事实。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占文华于7月21日给占军辉汇款20万元，日息1.8分，每5天结一次息。&ldquo;刚开始每10万元5天可得息9000元，20万元可得息18000元。存银行一年也得不到这么多利息。村里人大部分都尝到了甜头。&rdquo;占文华表示，占军辉最高还出过3分的日息。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;正因此，村民们纷纷投钱，有些人不仅把存款拿出来，还变卖家中的猪牛，甚至从外面借高利贷，再把钱投给占军辉。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;首先是占军辉的叔叔占基明一家，四个儿女凑了50多万元，借给了&ldquo;神通广大&rdquo;的侄子。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;看到占军辉的亲戚都在投钱，村民愈发确信，占军辉是在外面&ldquo;赚大钱&rdquo;。就连一直认为占军辉好吃懒做的占宝良，看见村里人都把钱投给占军辉，得到了不菲的利息时，也放松了警惕，&ldquo;他再怎么不靠谱，但能给村里人赚到钱，跟谁过意不去也别跟钱过意不去啊。&rdquo;9月21日，占宝良筹集了15万元，汇给了占军辉，日息1.8分，仅10天时间，他就得到了27000元利息。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;好景不长。占宝良在分到两次高额利息之后，却没有等来第三次付息。占军辉分别以国庆长假期间不计息等理由，不再向他们支付利息。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;国庆节后仍然没有利息的踪影，等来的却是一个灾难般的消息&mdash;&mdash;占军辉消失了。一夜之间，这些村子的生活被彻底打乱。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;生活巨变&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;自占军辉消失之后，占福良没有吃过一顿安心饭，没有睡过一个安稳觉，过着半逃亡的生活。&ldquo;现在每天都有人找我要钱，我们夫妻每天都是早出晚归，根本不敢回家，留下两个小孩，天天在家吃方便面。&rdquo;占福良说，他没有工作，这辈子都还不起这几百万。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占福良在占坊村算是经济宽裕的，很早就在崇仁县城买了房，在县城里做些小生意。他从8月份开始把钱投给占军辉，陆续投了314万元。这其中，有100多万是他自己的，将近200万是借来的。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占明华常年在浙江嘉兴打工，10月2日回到家乡，准备盖一栋两层小楼子。听说把钱存给占军辉能获利丰厚，心痒难忍，10月6日存了4万元给占军辉。&ldquo;刚存进去几天，就听说占军辉逃跑了，利息一分没拿到，本金也全部被骗。&rdquo;占明华说。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;周康兰，这个75岁的老太太，把自己的全部家当2万元钱，交给陈小强替她借给占军辉。听说自己的钱被卷跑了，周康兰就天天跟着陈小强。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;在这起民间高息借贷尚未崩盘时，投资人都能获得一些利润，在他们眼里，高额回报的诱惑远远大于内心的风险防御。当这个链条突然崩盘之后，生活瞬间发生了改变：借高利贷存款的人，有家不敢回；卖掉耕牛存款的人，有田不能种；不听老婆劝阻投钱的人，夫妻闹离婚&hellip;&hellip;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占军辉的家，也因为这起高息借贷事件，发生了变化。占军辉的叔叔占基明说，自从传出占军辉逃跑的消息后，他哥哥、占军辉的父亲占流明在村里就抬不起头来。&ldquo;他这阵子瘦了十多斤。我们也恨这个侄子，许多亲戚都被卷到里面来了，事情发生了，村里人很多都报了案，而作为亲戚，我们不好报案。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;占流明说，儿子小时候的脾气和性格也不差，是个讲义气的人。但是初中没有读完，就跟随着他们一起去了东莞种菜。&ldquo;后来我和他妈回家了，他自己留在东莞打工、做生意，但是也乱花钱。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;庞氏骗局&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;我知道有风险，也在网上看到过温州一带民间借高利贷崩盘的案子，但还是抱着侥幸心理。&rdquo;黄军生说，看到前面有人存了三四个月都没事，就冲进去，只想放一个月，赚几万元零花钱，&ldquo;但最后一个月就出事了&rdquo;。黄军生追悔莫及。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;江西省社科院经济研究所所长麻智辉认为，大部分读过一些书，有过在外打工经历的人，在借款时，都是抱着与黄军生一样的心态。在民间资本市场，以超出自己实际还款能力借高利贷，或者向亲友借钱，转手以更高的利息借给别人，这是典型的赌徒心理。&ldquo;而真以为把钱存到占军辉名下就能坐等高额利息是天上掉馅饼的人，只是村里一些中老年人，他们文化程度偏低，加上缺少信息来源，只要看到有人存钱获利，他们就会轻易相信，纷纷效仿。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;正是这种&amp;nbsp;&ldquo;赌徒心理+无知&rdquo;的结合，才酿成了这起亿元民间借贷大案。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;11月9日，《每日经济新闻》记者联系了崇仁县公安局。该局政工科科长陶莉表示，现在公安机关正在全力侦破此案。至于其他细节，需有上级公安部门的允许，才能接受采访。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;崇仁县委宣传部部长董运华在接受记者采访时说，这是一起典型的非法集资案件，本村人骗本村人，由于他们都没有借款的凭证，加之案件还未侦破，因此哪些人被骗了多少，还需最后核对。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;麻智辉说，在温州等江浙一带民间借贷崩盘案件频频曝光之后，这是江西省内第一起涉及资金过亿的民间借贷崩盘事件，虽然不能以此判断民间借贷从沿海大规模向内地转移的趋势，但是像江西这样的中部省份，民间借贷确有愈演愈烈之风。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;当前中小企业融资渠道艰难，当地政府应在正确引导民间资金进入市场，以及向公众普及非法融资的风险上做足文章，最大限度预防民间借贷崩盘事件发生。&rdquo;麻智辉说。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;消息来源：http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;2011年11月11日&amp;nbsp;02:34&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;color:#A5A5A5;padding:0px;margin:0px;&quot;&gt;每日经济新闻&lt;/span&gt;&lt;span style=&quot;color:#A5A5A5;&quot;&gt;微博&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;text-indent:2em;color:#A5A5A5;&quot;&gt;于垚峰&lt;/span&gt;&lt;/p&gt;', '', '一夜之间，&ldquo;占军辉卷款逃跑&rdquo;的消息让江西崇仁这个小县城炸开了锅，对航埠镇的占家村村民来说，不亚于8级地震。 占军辉长年在东莞打工，就是这个初中没毕业的28岁年轻人，在半年时间内，向占家村及航埠镇村民吸收存款数千万元，再加上崇仁县及在外务工人的存款，总额过亿元。 10月中旬后，占军辉便&ldquo;人间蒸发&rdquo;，这', '', 0, 0, 0, 5, 1, 1, '2012-04-28 04:39:21', '2012-04-21 06:05:37', 0),
(28, 1, 4, '河南担保公司高息融资对外放贷 资金链断裂被查', '&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;10月24日上午9时，郑州市公安局未来路派出所名门社区警卫室门前已经聚集了200多人，焦急等待着官方公布&ldquo;圣沃事件&rdquo;的最新进展。&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;他们参与&ldquo;投资理财&rdquo;的担保公司倒了。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;这是河南省今年继郑州&ldquo;诚泰事件&rdquo;、洛阳&ldquo;盛归来事件&rdquo;之后，又一起担保公司资金链断裂事件。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;一年&ldquo;融资&rdquo;10个亿&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;我悔得肠子都绿了，明明知道他们没证，我就贪那么点利息，才进去一个多月就出事了。&rdquo;冷风中，36岁的王英(化名)皱着眉头，瑟瑟发抖，她告诉《中国经济周刊》，8月初，她在河南圣沃投资担保有限公司(下称&ldquo;圣沃担保&rdquo;)投入了10万元&ldquo;理财&rdquo;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;与王英比起来，刘女士感觉更为窝火，她向《中国经济周刊》坦言，这就是一个&ldquo;骗子&rdquo;公司。9月初，她路过圣沃担保楼下，该公司的一名为段丽娜(音)的经理拉她理财，悲剧由此开始。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;她多次给我打电话，拉我高息理财，软磨硬泡。&rdquo;刘女士说，9月中旬，她前往圣沃担保了解情况，工作人员刚开始说可以三天短拆或五天短拆，利息诱人，后来说只剩下44天的短拆了，最终&ldquo;经不住诱惑&rdquo;，她选择了26天短拆，投进去200万元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;当时他们说得天花乱坠，就像洗脑一样，晕了，脑袋一点也不清醒，等出来后想想不对劲，借钱方不是法人，而且合同已经签好对方的名字了。&rdquo;刘女士离开圣沃担保后意识到合同&ldquo;有问题&rdquo;。不过，等她回去想退出的时候，为时已晚。尤其令她气愤的是，这位理财经理在拉她到圣沃担保&ldquo;理财&rdquo;之前，警方已经开始对该公司进行调查。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;事实上，圣沃担保仅仅用了一年时间，就从天堂走向地狱。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;《中国经济周刊》调查了解到，这是一家&ldquo;母女公司&rdquo;，成立于2010年9月1日，注册资本为5000万元，公司负责人为于兆筠和王雨，两人系母女关系。尽管起步较晚，圣沃担保却&ldquo;后来者居上&rdquo;。据知情人士透露，该公司目前涉案资金保守的数字为10.3亿。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;有的做了很多年的担保公司也达不到10个亿，这个刚刚起步的公司能在一年时间吸引这么多理财资金，是相当惊人的。&rdquo;一位担保公司从业人员坦言。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;投资者老周沮丧地告诉记者：&ldquo;都是高息惹的祸。现在钱放着都贬值了，他们给的月息是2分起步，最高可能达到月息6分，甚至一毛，谁不动心？&rdquo;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;巨额资金流向不明&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;《中国经济周刊》调查发现，圣沃担保吸引的理财资金来源可谓五花八门，除了部分投资者的自有闲散资金外，还有部分&ldquo;养老钱&rdquo;，甚至贷款资金。一位不愿具名的投资人告诉记者，他爱人把家里的房子抵押贷款，另外筹措了亲戚朋友的一部分钱，总计投入了100多万元。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;采访中，记者了解到这些投资者参与投资理财的方式亦各不相同。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;其中，大部分人签订的合同借款人为同一人，但很少有人见到借款人，多为出借人直接与圣沃担保签订合同。有些是以&ldquo;联合理财&rdquo;名义，以类似&ldquo;上下线&rdquo;的方式&ldquo;委托理财&rdquo;。上述投资人刘女士告诉记者，据圣沃担保工作人员对她的描述，一般以1000万元为一个高点，存款额达到1000万元即可拿到最高息。但最高息多少，刘女士不愿透露。此外，以200万元为起点，凡是能拉来存款的也可以吃&ldquo;下线&rdquo;的&ldquo;息差&rdquo;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;圣沃担保事发后，&ldquo;上下线&rdquo;吃&ldquo;息差&rdquo;浮出水面。一女士坦言，她拿了10万元给表姐，由表姐负责在圣沃担保&ldquo;理财&rdquo;，她手上无借据、无合同，表姐每个月给她3000元利息。出事后她才知道，表姐还从中吃了部分息差。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;在24日的案情通报会上，警方称自10月13日起已开始立案侦查圣沃担保非法吸收公众存款案，专案组多达200余人。目前，已抓捕24名涉案人员。警方称该公司管理混乱、账目不清，未透露具体涉案金额及资金流向。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;圣沃担保的巨额资金流向何方？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;据知情者透露，圣沃担保高息融来的资金，又以更高的利息放贷给了需要资金的企业，以郑州、洛阳等地的房地产企业居多。而在此前召开的信息发布会上，相关人士曾表示圣沃担保有一部分资金流向了郑州宇通等三家当地知名企业，但此番通报并未提及。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;同日，郑州宇通集团官方网站发布声明，称&ldquo;郑州宇通集团及所有下属企业均未向任何外部担保机构直接或间接借入任何资金，并承诺将来也不会从担保机构借入资金&rdquo;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;资金的&ldquo;过桥&rdquo;与&ldquo;过夜&rdquo;&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;9月17日，圣沃担保的&ldquo;东窗事发&rdquo;已引起了&ldquo;连锁反应&rdquo;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;《中国经济周刊》调查发现，目前，河南已有一些担保公司出现不同程度的挤兑现象。在担保公司聚集的郑州财富广场、华悦时代广场等高档写字楼内，投资者到担保公司追要理财款的现象已不鲜见。河南宝银投资有限公司因资金紧张，其董事长谢国银竟然两次遭到投资者挟持&ldquo;逼债&rdquo;。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;高息融资的公司大部分都是拆东墙补西墙，靠不停地吸纳新客户，新资金来归还到期的本、息，而后面的客户新注入资金一旦无法维系前面到期的本、息，就会引起投资者的恐慌。此时发生挤兑，他们的资金链条必然会断裂。&rdquo;一位业内人士告诉记者，&ldquo;出事是早晚的，就像吹气球一样，越吹越大，早晚会破。&rdquo;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;而高息的背后是一个利益链条，折射出来的则是金融秩序的混乱。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;一位熟悉内情的人士告诉记者，目前&ldquo;过桥资金&rdquo;、&ldquo;过夜资金&rdquo;等在金融业内已是公开的秘密。所谓&ldquo;过桥资金&rdquo;就是很多企业在向银行借贷后，由于发展周期较长，贷款到期时无力偿还贷款，为了保证银行信用，就借担保公司的资金还款。将银行的贷款还上后，银行再重新发放贷款。此间，银行、企业、担保公司达成默契，用钱多不足一个月时间，而日息则达到2分甚至3分。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;知情人士向《中国经济周刊》透露，圣沃担保负责人王雨曾称，圣沃公司资金链断裂的原因在于近期银根紧缩，存款准备金率上调，货币流量急速减少，银行从中撤出，三方融资链条同时断裂，而该公司借贷出去的钱则一时无法收回。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;line-height:2em;&quot;&gt;&lt;strong&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;监管乏力？&lt;/span&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;目前，我国中小企业&ldquo;融资难&rdquo;依旧无解，信用担保和民间借贷已经成为中小企业经营发展的救命稻草。不过，&ldquo;井喷式&rdquo;增长加剧了同业间的恶性竞争，再加上相关部门监管薄弱，担保行业乱象由此横生。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;&ldquo;任何事物的发展都有一个过程，由乱到治。目前，我们应该做的是提振信心，并以此为基础进行整顿，清理市场上不合规的公司，严厉打击非法集资的公司，促使担保行业健康有序发展。&rdquo;河南省民营经济研究会秘书长张立功在接受《中国经济周刊》采访时坦言。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;早在2010年3月，国家银监会、发改委等七部委就联合出台了《融资性担保公司管理暂行办法》。是年12月7日，河南省政府办公厅亦发布《关于开展全省中小企业信用担保机构规范整顿工作的通知》，对该省担保行业进行大规模清理整顿。2011年7月28日，河南省又出台了《河南省融资性担保公司管理暂行办法》，明确规定了融资性担保公司的准入门槛、风险防控，以及政府监督的具体措施。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;font-family:宋体, simsun;&quot;&gt;不过，风口浪尖之际，河南多家担保公司却相继出事，&ldquo;监管乏力&rdquo;由是成为惯常评判。据财政部相关人士透露，该部目前正与有关部委修订《中小企业信用担保资金管理暂行办法》，新的管理办法将首次明确提出重点扶持小微企业，并针对担保机构开展小微企业融资担保业务进行扶持和补贴。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span style=&quot;color:#BFBFBF;font-family:宋体, simsun;&quot;&gt;消息来源：&lt;/span&gt;&lt;span style=&quot;color:#BFBFBF;font-family:宋体, simsun;&quot;&gt;http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;color:#BFBFBF;font-family:宋体, simsun;&quot;&gt;&amp;nbsp;2011年11月01日&amp;nbsp;00:01&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span style=&quot;color:#BFBFBF;font-family:宋体, simsun;&quot;&gt;中国经济周刊&lt;/span&gt;&lt;span style=&quot;color:#BFBFBF;font-family:宋体, simsun;&quot;&gt;微博&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '10月24日上午9时，郑州市公安局未来路派出所名门社区警卫室门前已经聚集了200多人，焦急等待着官方公布&ldquo;圣沃事件&rdquo;的最新进展。 他们参与&ldquo;投资理财&rdquo;的担保公司倒了。 这是河南省今年继郑州&ldquo;诚泰事件&rdquo;、洛阳&ldquo;盛归来事件&rdquo;之后，又一起担保公司资金链断裂事件。 一年&ldquo;融资&rdquo;10个亿 &ldquo;我悔得肠子都绿了，', '', 0, 0, 0, 3, 1, 1, '2012-04-26 03:40:13', '2012-04-21 06:09:10', 0),
(29, 1, 4, '11月以来人民币实际贬值0.5%', '&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;欧债危机加剧正在继续改变国际资金流向，&ldquo;人民币只涨不跌&rdquo;的市场一致预期也随之发生变化。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;《经济参考报》记者根据公开数据计算得出，截至11月25日，按当日1美元兑6.3554元人民币的中间价计算，11月以来人民币实际贬值0.5%。与此同时，境外人民币无本金交割远期合约NDF也显示，未来1个月到2年的人民币对美元汇率走低，显示远期市场出现人民币贬值预期。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;现在，对人民币升值前景的判断出现和以往不一样的声音。《华尔街日报》11月25日在显要位置刊发题为《人民币升值过程或将戛然而止》的署名文章，分析师称，中国10多年来才出现第二次外汇净流出，这或许意味着人民币逐渐升值的日子即将结束。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;文章援引东方汇理分析师科瓦尔奇克的话说，人民币币值很容易受到国际资本流动的影响，升值压力已经没有过去那么大。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;科瓦尔奇克所说的&ldquo;第二次外汇净流出&rdquo;指的是央行最新公布的金融机构外汇占款数据。据央行初步统计，10月末金融机构外汇占款总额比9月末净减少248.92亿元，自2008年1月以来首次出现月度负增长。&ldquo;资金流出的结果就是，2010年6月份以来人民币对美元以每年5.5%的速度升值的过程行将结束。相对地，人民币币值的波动性可能会升高，从而令下注&lt;span style=&quot;color:#000000;&quot;&gt;赌人民币升值的投资者面临的风险加大。&rdquo;科瓦尔奇克说。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;而渣打银行发布的报告称，2012年人民币升值速度可能仅为3.3%，低于今年4.5%的升值预期。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;市场主体的行为也反映出人民币升值预期或有改变。&lt;/span&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;国家外汇管理局11月25日公布的统计数据&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;显示，今年10月银行代客结售汇顺差仅32亿美元，创下该数据按月公布以来的新低。具体而言，10月的代客售汇数据与8月和9月持平，但代客结汇数据却明显下滑，这成为结售汇顺差创下新低的主要原因。实际上，若人民币升值预期较强，企业和个人倾向将外汇卖给银行，即银行结汇；而当市场预期人民币贬值时，企业和个人则愿意手持美元，从银行买入外汇，即银行售汇。由此看出，结汇下滑表明市场主体不再一致看多人民币。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;而作为资金流入的最主要组成部分，海关总署日前公布的进出口数据显示，10月出口增速延续低迷之势，当月贸易顺差170.33亿美元，远低于市场预期。在贸易顺差下降的趋势下，人民币升值步伐放缓在情理之中。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;color:#000000;&quot;&gt;中国人民大学国际货币研究所理事兼副所长向松祚&lt;/span&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;日前在微博中表示&lt;/span&gt;&lt;span style=&quot;color:#000000;&quot;&gt;，&ldquo;从市场情绪、贸易收支、国际资金流动等几方面观察，我以为人民币升值势头已经基本结束，至少阶段性见顶。从我国明年外贸形势、欧债危机诱发资金外流、国内房地产调控&lt;/span&gt;导致资产价格普遍开始回落等多方面判断，也应该立即停止单边升值。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;路透社25日援引巴克莱资本最新报告指出，&ldquo;新的人民币汇率制度&rdquo;(包括允许更大范围的双向汇率波动；更多地使用货币篮子，允许兑美元的即期汇率更大的波动范围)可能不久就会形成，这可为双向货币波动铺平道路。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;路透社报道称，随着外部盈余的缩小和资本流入的放缓，人民币已不再被明显低估。过去两个星期，市场即期汇率曾多次被交易到交易区间较弱的一端。值得注意的是，尽管巴克莱资本预期在贸易加权的基础上人民币年均升值5%至10%，但也表示，在新的人民币汇率制度下，经济和金融形势的不利变化可能诱发多次的货币贬值。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;不过，在国际金融问题专家赵庆明看来，人民币仍将保持升值态势。首先，中国国际收支顺差的格局仍将持续；其次，人民币目前仍被低估，距均衡汇率还有一定距离；第三，目前热钱比较&ldquo;安静&rdquo;，有利于人民币实现均衡汇率。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;2011年11月28日&amp;nbsp;01:24&lt;/span&gt;&lt;span style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;background-color:#ffffff&quot;&gt;&lt;span style=&quot;color:#BFBFBF;padding:0px;margin:0px;&quot;&gt;经济参考报&lt;/span&gt;&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '欧债危机加剧正在继续改变国际资金流向，&ldquo;人民币只涨不跌&rdquo;的市场一致预期也随之发生变化。 《经济参考报》记者根据公开数据计算得出，截至11月25日，按当日1美元兑6.3554元人民币的中间价计算，11月以来人民币实际贬值0.5%。与此同时，境外人民币无本金交割远期合约NDF也显示，未来1个月到2年的人', '', 0, 0, 0, 1, 1, 1, '2012-04-28 03:22:47', '2012-04-21 06:11:53', 0),
(30, 1, 4, '约十家中小银行报会 上市开闸呼声日高', '&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;核心提示：&lt;/strong&gt;创新投资集团靳海涛提出，&ldquo;应该支持创投机构、小微银行、担保公司、小额贷款公司、融资租赁公司登陆资本市场。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;到目前为止，所有报给我们的(城商行)上市申请，符合标准的我们都会同意，现在已有9-10家报证监会。&rdquo;12月1日召开的第九届中小企业融资论坛上，银监会银行监管二处处长张海川向记者透露。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;当天的论坛上，长江商学院教授王一民、中国政法大学教授刘纪鹏等多位学者的发言，使得中小银行上市重新开闸的呼声集中体现。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;出席会议的深圳市创新投资集团董事长靳海涛提出，&ldquo;应该支持创投机构、小微银行、担保公司、小额贷款公司、融资租赁公司登陆资本市场。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;2007年，&lt;span id=&quot;stock_sz002142&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;宁波银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sz002142&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;9.46&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.04&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.42%&lt;/span&gt;)&lt;/span&gt;(002142.SZ)、&lt;span id=&quot;stock_sh601009&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;南京银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sh601009&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;9.28&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.20&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;2.20%&lt;/span&gt;)&lt;/span&gt;(601009.SH)、&lt;span id=&quot;stock_sh601169&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;北京银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sh601169&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;9.67&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.03&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.31%&lt;/span&gt;)&lt;/span&gt;(601169.SZ)相继上市，让全国一百多家中小银行看到了希望。但此后再无中小银行获批上市。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;深交所综合研究所11月发布的《关于城市商业银行发行上市问题的调研报告》显示，该所近期对城商行发行上市问题进行了调研。报告认为，城商行发行上市有利于充分发挥资本市场对中小和微小企业的支持作用。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;小微银行对小微企业的支持是非常大的，效益也很好。但是，小微银行非常难于上市，所以深创投也不敢投资，希望这方面能给予宽松政策。&rdquo;&amp;nbsp;靳海涛呼吁。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong&gt;约十家中小银行报证监会&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;全国147家城商行中，不少城商行都有强烈的上市冲动。据不完全统计，各种场合提出有上市意愿的城商行超过40家，但2007年三家城商行上市后，也引来一些争议。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;其中，跟股权相关的争议最明显，如北京银行的娃娃股东问题，宁波银行&ldquo;小非&rdquo;解禁时诞生91个千万富翁和896个百万富翁等。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;股权的复杂、职工持股比例高是城商行上市的一大难点。城商行多由城市信用社演化而来，城商行股东人数众多，历史沿革中股份变动方式也很多样：有转让双方直接签订转让协议的；有转让方委托公司转让持有股份的；还有因法院裁定、清算投资、股东本身被合并、分立或被兼并导致的股东变更等多种情形。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;股权混乱也导致大部分城商行存在股东人数超标的情形。根据《公司法》和《证券法》，股东人数超过200人的公司就已构成公开发行。上市审核中，股东人数超标被视为硬伤，无法过会。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;针对城商行股权方面的问题，监管部门已有相关说法，如2010年9月财政部等四部委发布的《关于规范金融企业内部职工持股通知》(业内称&ldquo;97号文&rdquo;)要求，公开发行新股后内部职工持股比例不得超过总股本的10%，单一职工持股数量不得超过总股本的1&permil;或50万股&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;(按孰低原则确定)，否则不予核准公开发行新股。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;一批拟上市城商行积极解决内部职工股问题已有成效，从我们掌握的情况看，若按10%或25%的发行比例计算，部分城商行现已能满足97号文关于上市后内部职工持股比例不超过总股本10%的要求。&rdquo;上述报告认为。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;关于200人股东的问题，监管部门也给予特事特办。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;据报道，对于城市商业银行上市涉及股东人数超过200人问题，银监会已和证监会达成共识，以新《公司法》实施为界，2006年以前股东超过200人的，可以申报IPO材料。其他公司申请上市，必须先清理超限股东。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;至此，城商行上市的主要障碍均已扫清。&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;中小银行上市何时开闸&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;因为历史的原因，很多人对中小银行的治理印象不佳，特别是2010年底&ldquo;齐鲁银行诈欺案&rdquo;及随后的&ldquo;汉口银行被指作假担保&rdquo;的事件爆发后。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;对此，会上张海川通过白描式的数据罗列，还原了城商行全貌。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;城商行的各项主要指标好于很多人的想象。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;张海川透露的数据显示，城商行资本利润率为21.88%，有十几家城商行的资本利润率超过40%，超过50%的也有五六家；城市商业银行的总资产利润率目前为1.42%，这是高于国有商业银行的1.41%和股份制银行1.29%的总体水平，42家城商行的总资产利润率超过2%，有5家城商行的资产利润率超过3%，最高达3.8%，即便在国际同业也是相当高的；城商行的资本充足率现在超过13%，而按照监管的要求是10%，贷款损失准备充足率322.74%，拨备覆盖率达298%，这意味着拨备超过不良贷款的3倍。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;这是一组简单数据，就城市商业银行总体而言，目前公开的数据来看，相当良好和相当健康。&rdquo;&amp;nbsp;张海川认为。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;城商行的盈利情况要好于现已上市的大型商业银行和股份制商业银行。银监会2010年年报数据显示，2008-2010年，城商行的税后利润增长率分别为64.4%、21.7%和55%，同期大型商业银行的三年增速分别是43.6%、13%和28.7%，股份制商业银行的三年增速数据分别是49.1%、9.9%和46.8%。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;除质地不错外，中小企业及小微企业的融资难问题，也成为中小银行上市松绑的理由。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;银监会的数据显示，城商行的小企业贷款余额为1.38万亿元，占全部企业贷款余额的46.8%。城商行的小企业贷款占整个银行业系统小企业贷款的15%，远高于城商行8.34%的市场份额。&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;资本市场难以直接为小型企业、微型企业提供金融支持，但通过吸纳城商行上市，可以间接支持小型企业和微型企业，并收到&lsquo;以点带面&rsquo;的成效。&rdquo;深交所综合研究所上述报告认为。&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;2011年12月02日&amp;nbsp;00:20&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;21世纪经济报道&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '核心提示：创新投资集团靳海涛提出，&ldquo;应该支持创投机构、小微银行、担保公司、小额贷款公司、融资租赁公司登陆资本市场。&rdquo; &ldquo;到目前为止，所有报给我们的(城商行)上市申请，符合标准的我们都会同意，现在已有9-10家报证监会。&rdquo;12月1日召开的第九届中小企业融资论坛上，银监会银行监管二处处长张海川向记者透露', '', 0, 0, 0, 0, 1, 1, '2012-04-21 06:14:07', '2012-04-21 06:14:04', 0),
(31, 1, 4, '银监会官员露口风 城商行IPO或重启', '&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;自首批城商行A股上市之后，已经四年没有新的城商行完成A股IPO，尽管从去年开始就已经盛传城商行IPO将会重新开闸，但是一直至今，也没有新的城商行或者农商行完成A股IPO。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;昨日(12月1日)银监会银行监管二部处长张海川在深圳第九届中小企业融资论坛上称，城商行上市符合各方利益，只要符合标准银监会就放行。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;&ldquo;我们对上市一直都是很积极主动的。&rdquo;华南某农商行人士接受《每日经济新闻》记者采访，非常直接地表达了他们对于上市的热情和支持。而城商行与农商行上市情况相似。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;相关分析师也认为，从目前来看，小银行由于主要是开展中小企业融资，结合目前政策来看，IPO放宽还是很有可能的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;IPO将重启？&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;日前，北京农商行公布，拟于12月6日召开临时股东大会，审议《关于成立北京农村商业银行股份有限公司IPO领导小组及工作组的议案》，这家中国最大的农商行上市工作启动在即。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;随着尚福林由证监会主席转任银监会主席后，业内也传出城商行IPO大门或将重启的声音。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;2007年，在&lt;span id=&quot;stock_sh601009&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;南京银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sh601009&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;9.28&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.20&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;2.20%&lt;/span&gt;)&lt;/span&gt;(601009，SH)、&lt;span id=&quot;stock_sz002142&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;宁波银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sz002142&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;9.46&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.04&lt;/span&gt;,&lt;span style=&quot;color:red;padding:0px;margin:0px;&quot;&gt;0.42%&lt;/span&gt;)&lt;/span&gt;(002142，SZ)、&lt;span id=&quot;stock_sh601169&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;span style=&quot;color:#000000;padding:0px;margin:0px;&quot;&gt;北京银行&lt;/span&gt;&lt;/span&gt;&lt;span id=&quot;quote_sh601169&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;9.67&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.03&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.31%&lt;/span&gt;)&lt;/span&gt;(601169，SH)几家银行顺利上市完成IPO之后，再也没有新的城商行登陆A股完成IPO。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;&ldquo;我也听说了，目前是正在加快城商行上市这类事情。&rdquo;某券商银行业研究员表示。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;昨日张海川在上述论坛上称，服务中小企业、服务地方经济、服务城市居民是城商行的&ldquo;天赋使命&rdquo;。同时他表示，&ldquo;到目前为止所有报我们这里的上市申请，符合标准的都会同意，因为银监会也有一些标准，比如对于他的评级，有没有内控失效的案例等。凡是符合标准的我们都会发路条。&rdquo;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;前述研究员表示，小银行的中小企业贷款所占比例比较大，比较符合目前的政策，所以说放宽还是有可能的。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;上市符合各方利益&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;张海川指出，城商行上市相关的各个利益方没有人会反对，因为它符合绝大多数人的利益。具体来说，对于股东来说，他实现了股权的价值发现，也有了新的退出渠道；同时也符合监管部门的利益；银行上市也符合客户的利益。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;深圳证券交易所综合研究所副所长阙紫康发言时也指出，2007年北京银行、宁波银行、南京银行上市的时候，投资者会如何看待城商行并不是没有疑问的。令人欣慰的是，几年的发展下来，这几家城市商业银行尽管股价有起伏，但是最终还是得到了市场的认可。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;他认为，要认识到这145家城商行的投资价值，看到他们的大趋势。中国中小企业的金融服务、小微企业的金融服务已经呈现产业化的发展态势，而且会加速，整个金融业当中这是一个新的增长点，是中国金融业发展的一片蓝海。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;line-height:2em;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;股权因素制约上市&lt;/strong&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;前述银行人士表示，目前正在准备上市，但是没有具体的时间表。&ldquo;我们对上市是积极主动的态度，主要还是要看监管部门的意见。&rdquo;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;前述研究员表示，目前来说，城商行上市需要证监会、银监会、央行等各方面的批准。除了这些政策因素，目前制约城商行上市的主要问题就是股权结构的问题。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;他表示，之前就有好几家城商行因为股东数量的问题而暂缓了IPO。据了解，有的城商行是城市信用社、农村信用社改组过来的，零散的股东和个人股东比较多。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span style=&quot;padding:0px;margin:0px;&quot;&gt;去年9月，财政部和一行三会发布了&amp;nbsp;《关于规范金融企业内部职工持股的通知》，厘清了城商行上市前内部职工持股的清理规范标准，被业界认为是扫清城商行上市障碍的文件之一。&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;text-indent:2em;text-align:left;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;2011年12月02日&amp;nbsp;02:19&lt;/span&gt;&lt;span style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;每日经济新闻&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '自首批城商行A股上市之后，已经四年没有新的城商行完成A股IPO，尽管从去年开始就已经盛传城商行IPO将会重新开闸，但是一直至今，也没有新的城商行或者农商行完成A股IPO。 昨日(12月1日)银监会银行监管二部处长张海川在深圳第九届中小企业融资论坛上称，城商行上市符合各方利益，只要符合标准银监会就放行', '', 0, 0, 0, 2, 1, 1, '2012-04-28 03:40:18', '2012-04-21 06:16:19', 0),
(32, 1, 4, '南京女老板被指连设三局集资数亿元后失踪', '&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;昨天上午，南京白下区锦江丽舍公寓7楼东南角办公区，来自浦口的讨债人老张使劲拽了拽标有&ldquo;总经理&rdquo;字样的办公室门，当无法打开房门后，他一屁股坐在旁边的办公桌上，一声叹息：40万难要回了。而在数百平方米的办公区内，稍微值钱的物品都被搬空，曾在此办公室募集资金的老板尤雯(化名)已于几天前失踪，涉及资金据称高达数亿元。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;值得一提的是，在失踪前半个多月，尤雯还以资金周转困难，号召大家募集资金自救，后来数百人排队借钱给她。拿到最后这笔自救资金后不久，她又以廉价售卖股权的方式，获得大量资金，随后神秘消失。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;让人崩溃&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;&ldquo;借走&rdquo;他们的钱女老板失踪了&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;尤雯可能卷款跑路了，现在联系不上她，她儿子也好久没去上学了，据说已经移民，我们上当了。&rdquo;今年4月5日晚，南京鼓楼的市民靼宽(化名)接到朋友的这个电话后，一夜未眠。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;靼宽先后借给做医疗器械生意的尤雯140万元，如今只收回了40万元，还有100万元。无法电话联系上尤雯后，他到处打探得知，连她手下的业务员也没法联系上她了。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;一夜未眠后，第二天一大早，靼宽便来到珠江路&lt;span id=&quot;stock_sh600628&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;新世界&lt;/span&gt;&lt;span id=&quot;quote_sh600628&quot; style=&quot;padding:0px;margin:0px;&quot;&gt;(&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;7.57&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.03&lt;/span&gt;,&lt;span style=&quot;color:green;padding:0px;margin:0px;&quot;&gt;-0.39%&lt;/span&gt;)&lt;/span&gt;，因为在10多天前，他就是在该大楼内一间办公室，将80万元现金交给了尤雯。而等他到了新世界才发现，当初交钱的办公室门口已聚集了不少人，一打听才知，都是来找尤雯要钱的，数额从十万元到千万不等。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;无奈之举&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;寻求心理平衡&amp;nbsp;&lt;/strong&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;债主搬走椅子&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;昨天上午，数百投资人聚集在白下区大光路锦江丽舍公寓尤雯曾经的办公地点，商量对策。一些投资者则选择了报警求助。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;昨天下午1点多，在锦江丽舍公寓7楼东南角，尤雯曾经的办公区内，一名投资者正将一台复印机往楼下搬。而一老太太着带着家人，将会议室内的两把木椅搬了出来。&ldquo;几十万都搭进去了，拿两把椅子也没用，但心理上会平衡一点。&rdquo;老太太称，走到如今这一步，不但养老钱没了，自己还欠了亲朋好友一大笔债。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;在办公区二楼，总经理办公室的门一直锁着，门暂时还未被撬开。办公室内，一把银光闪闪的老板椅外加一张欧式古典风格的沙发，可以看出当时办公室主人是何等的气派。靼宽想搬走总经理办公室那把老板椅来寻求心理安慰，可门却紧锁着，无法打开。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;&ldquo;借钱&rdquo;三连环&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;1&amp;nbsp;高利息募资&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;女老板月利5%募资&lt;/strong&gt;&amp;nbsp;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;众人纷纷掏钱&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;他将60万元存款取出来，在朋友介绍下，交给了尤雯，换来的是一张盖有某医疗器械公司公章，并摁有一个手印的借条&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;据靼宽介绍，欠他们钱的尤雯今年40岁，南京人，家住鼓楼龙江地区。2010年10月，一个年轻女子找到靼宽的一个朋友，称有一个老板在做医疗器械生意，主要是疤痕膏和食道散，公司摊子铺得很大，急需资金，以月利息5%的高回报向社会募集资金，单笔资金最低10万元。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;当时股市不好，楼市价位也高得令人心颤，我朋友正好有资金在手上闲着，便动了心。&rdquo;2010年10月底，靼宽朋友在珠江路新世界大楼4楼一间办公室，试探性将10万元资金借给了一个中年女子，她就是后来开宝马做大生意的神秘老板尤雯。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;起初尤雯很讲信用，每月到了约定的时间，就将5000元利息打到靼宽朋友的卡里。&ldquo;我听说后动了心。&rdquo;靼宽回忆，后来他背着家人，将60万元存款悄悄取出来，在朋友介绍下，交给了尤雯，换来的是一张盖有某医疗器械公司公章，并摁有一个手印的借条。自从借钱给尤雯后，靼宽每月都能按时收到3万元利息。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;今年春节前，靼宽在清理自己的账户时，发现去年12月份的3万元利息未到账，便打电话询问尤雯。她称自己不但在做医疗用品，还在搞网络公司，摊子铺得很大。可因医院改制导致回款慢，最近资金周转困难，加上春节即将到来，各单位结算繁忙，所以利息和到期本金只能在今年7月份支付。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;因到期的本金和利息无法归还，不少投资者开始担心，因为他们发现自从投资尤雯的公司以来，拿到的借条大多是尤雯以个人名义写的，连公司的公章都没有。投资者春节后找到尤雯位于新世界大楼的办公室，希望她能尽快归还本金，但大多数投资者连尤雯的面都没能见上。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;有头脑清醒的投资者担心她跑路，便开始调查她家里的情况。有人通过关系，发现她儿子在南京某高校上学，便相信她会全力解决危机，不会卷款跑路。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;2&amp;nbsp;&ldquo;自救&rdquo;局中局&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;想要以前的钱？&lt;/strong&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;得再拿出钱来&ldquo;自救&rdquo;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;大家再出钱作为自救款给她，每10万换回先前本金3万，自救出来的本金当场得款，自救资金与产生的利息半月后返还&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;据靼宽介绍，后来有投资者发现，尤雯在南京市区还有另外一处办公点，位于白下区大光路锦江丽舍7楼。消息传开后，陆续有投资者到锦江丽舍找尤雯。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;为了让大家放心，今年3月14日，尤雯组织了数百名投资者，专门召开了一个投资者商谈会。会上，尤雯泪流满面地称，自己和丈夫为了筹钱，几天前在徐州遭遇车祸，现在丈夫成了植物人躺在外地的医院，自己头部也受伤。说完自己悲惨的遭遇后，尤雯抹掉眼泪，称为了渡过难关，她特意请高人帮忙制定了投资者自救方案。&ldquo;大家出钱作为自救款给她，每10万换回先前的本金3万，自救出来的本金当场得款，自救资金与产生的利息半月后返还。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;为了早点救出自己的本金，很多人跟我一样，早上6点就去她的公司排队，她则组织手下的人，上午和下午各发一次号，每天接纳180人参与自救。&rdquo;靼宽称，从3月15日到3月30日，先后有数百人参加自救。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;现在想起来，自救方案只是她设的一个新套，再次把我们拉入陷阱。&rdquo;靼宽称，当时自己借给了她60万元，为了自救，他后来筹集了80万自救款交给了尤雯，救出了先前的本金和利息共计40万元。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;按照自救协议，我80万自救款应该在4月1日还给我，可到了时间后，她却还不出钱来。&rdquo;靼宽称，这样不但先前的60万本金没能救出来，反而贴了40万进去。有投资者估算，通过此次自救方案，尤雯至少又是数千万资金到手。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;3&amp;nbsp;&ldquo;卖股份&rdquo;再套&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;以回报20%为诱惑&amp;nbsp;&lt;/strong&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;售卖网络公司股份&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;她称网络股份公司发展潜力巨大，以每股1元贱卖网络公司股份，同时只要参与认购网络公司股份的，每万股送2000股&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;4月2日，不少自救款到期无法偿还，但尤雯并未罢手，而是进行着最后的疯狂。她满怀激情地称，自己找到了新的投资项目，搞网络股份公司，潜力巨大。不过她称，现在因缺少周转资金，决定以每股1元贱卖网络公司股份，同时只要参与认购网络公司股份的，每万股送2000股。同时还对股价今后的浮动和公司运行做出了一系列承诺。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;此诱惑一出，不少投资者又开始给她投钱。据投资人张某介绍，尤雯利用此举，至少又获得了数百万现金。拿到这笔钱后，尤雯承诺在4月10日，向大家兑现此前的自救款和利息。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;就在大家盼着拿回钱时，4月5日晚，有人发现尤雯再也联系不上了，而且家里和办公地点都无人。此消息一出，大家都慌了。有投资者找到了她丈夫，不过对方称，已与尤雯协议离婚多日，两人之间不再有关系。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;后来，有投资者调查发现，4月9日前，她先后在白下和下关住过酒店，此后再无酒店入住记录。当投资者找到她儿子所在的学校时，校方称她儿子已休学，具体去向不知道。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;最新进展&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;涉款可能数亿&lt;/strong&gt;&amp;nbsp;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;警方已介入调查&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&ldquo;我们借给她钱的人，大部分不知道她到底是个什么样的人，公司到底是做什么的，甚至连面都没见过。&rdquo;一自称尤雯欠他40万的投资者称，他曾有幸见过一次，尤雯开着价值百万的宝马，虽然年近40，但风韵犹存，看上去也就30出头的样子。&ldquo;举手投足之间，显示出了几分成功女士的特征，可以说谈吐大方，气质不凡。&rdquo;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;投资者发现，他们在参与自救方案时，先前借条上落款为医疗器械公司的借条被尤雯收了回去，全部换成了她个人签名和盖私章的借条。据此，不少投资者认为，她此举有预谋，因为以个人名字打的欠条，到最后只能算是个人借款，她认账不还钱，拿她也没办法。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;昨天，有投资者调查发现，尤雯此前开的宝马轿车，以极低的价格卖掉了，在河西的房子也卖掉了。尤雯到底欠多少人的钱，涉及到的金额是多少，没人能说清楚。不过，据其手下一业务员介绍，她经手的资金只有1000多万元，算是很少的，好几个业务员经手的金额都高达亿元，保守估计总额在5亿元以上。不少投资者称，如果此前尤雯称资金周转困难，延期还本付息，最多算违约。可后来她搞自救方案后，又出售卖网络公司股份，接着便失踪，明显存在预谋，就有诈骗嫌疑了。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;目前，不少投资人都到白下公安分局报警，警方已介入调查。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;涉嫌集资诈骗&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;strong style=&quot;padding:0px;margin:0px;&quot;&gt;昆山一女老板被拘&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;今年2月3日以来，昆山市公安局经侦大队陆续接到10余名群众反映，昆山市陆家镇人陈春芳以做生意资金紧张为由，以支付高额利息为诱饵，向他人借得大量钱财后无法偿还，随后陈春芳突然失踪。2月27日，昆山市公安局依法受理该案，并立即进行初查。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;经调查，昆山市公安局经侦大队发现陈春芳家中有大量房产、汽车及高档商品，另外发现自2011年2月起，陈春芳频繁出境至澳门，并在澳门使用&lt;a href=&quot;http://finance.sina.com.cn/creditcard/&quot; target=&quot;_blank&quot; style=&quot;color:#256eb1;text-decoration:none;padding:0px;margin:0px;&quot;&gt;信用卡&lt;/a&gt;套现取出大量现金用于赌博，并将借来的资金用于偿还欠款及购车、购房，陈春芳有集资诈骗嫌疑。今年3月2日，昆山市公安局对陈春芳涉嫌集资诈骗罪立案侦查，依法对陈春芳办理了上网追逃手续。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;3月5日，在苏州市有关部门的协助下，昆山市公安局经侦大队在苏州市工业园区将陈春芳成功抓获。经依法侦查查明，犯罪嫌疑人陈春芳自2011年2月起以做生意需要资金周转为由，以日息0.3%至0.5%或月息3.5%至5%的高额利息为诱饵，分别向庄某、唐某等10余人借款人民币8000余万元，用于其在澳门赌博、偿还欠款及购买奢侈品，造成经济损失4000余万元。目前，犯罪嫌疑人陈春芳已被刑事拘留。&lt;/p&gt;&lt;p style=&quot;color:#333333;font-family:verdana, arial, tahoma;font-size:14px;line-height:25px;white-space:normal;background-color:#FFFFFF;text-indent:2em;text-align:left;padding:0px;margin-top:0px;margin-bottom:0px;&quot;&gt;&lt;span id=&quot;art_source&quot; style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;http://www.sina.com.cn&lt;/span&gt;&lt;span style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;pub_date&quot; style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;2012年04月13日&amp;nbsp;10:10&lt;/span&gt;&lt;span style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;&amp;nbsp;&amp;nbsp;&lt;/span&gt;&lt;span id=&quot;media_name&quot; style=&quot;font-family:verdana, arial, tahoma;font-size:14px;background-color:#FFFFFF;color:#BFBFBF;&quot;&gt;新华网&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '昨天上午，南京白下区锦江丽舍公寓7楼东南角办公区，来自浦口的讨债人老张使劲拽了拽标有&ldquo;总经理&rdquo;字样的办公室门，当无法打开房门后，他一屁股坐在旁边的办公桌上，一声叹息：40万难要回了。而在数百平方米的办公区内，稍微值钱的物品都被搬空，曾在此办公室募集资金的老板尤雯(化名)已于几天前失踪，涉及资金据称高', '', 0, 0, 0, 7, 1, 1, '2012-04-28 04:39:13', '2012-04-21 06:19:56', 0),
(34, 1, 33, '农村金融机构全面风险管理建设规划', '&lt;p&gt;&lt;span style=&quot;font-size:16px;font-family:楷体;text-decoration:none&quot;&gt;农村金融机构全面风险管理建设规划&lt;/span&gt;&lt;br /&gt;&lt;/p&gt;', '', '农村金融机构全面风险管理建设规划', '', 1, 0, 0, 6, 0, 1, '2012-04-29 03:02:39', '2012-04-29 02:40:07', 0),
