INSERT INTO `cms_agency` (`id`, `title`, `category_id`, `image_url`, `introduction`, `rate`, `deleted`, `created_date`, `last_modified_date`) VALUES
(1, '金融风险管理研究所', 1, 'banner.png', '金融风险管理研究所围绕着新资本协议框架，基于流程银行的最佳实践，结合人工智能、数据挖掘技术和各种数据分析方法，研究经济及金融一体化趋势下金融风险的识别、度量和控制理论与应用，特别是金融信用风险评估指标体系、风险分析模型及预警系统、商业银行结构性风险管理系统、证券投资风险预测与管理工具等。研究所依托山东财经大学的学科优势，与相关企业、科研单位在金融机构全面风险管理方面开展产学研合作，促进科技成果的转化与应用。\n专家团队由具有较高理论造诣的高校教师和具有丰富实际工作经验的业界专家组成。其中教授2人，副教授、高级工程师和高级系统分析师6人，团队成员大多具有博士学位或具有海外学习与工作经历。\n', 0, 0, '2012-04-21 01:11:03', '2012-04-29 03:29:07'),
(2, '金融服务计算研究所', 2, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 01:11:03', '2012-04-21 01:07:47'),
(3, '计算金融研究所', 3, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 01:11:03', '2012-04-21 01:07:47'),
(4, '金融信息安全研究所', 4, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 01:11:03', '2012-04-21 01:07:47'),
(5, '金融数据管理研究所', 5, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 01:11:03', '2012-04-21 01:07:47'),
(6, '金融服务创新研究所', 6, 'banner.png', '2008年金融危机以来，全球金融市场大幅波动频繁发生，给世界经济和金融市场的健康发展造成了巨大的破坏，对金融风险进行严格管控成为各国领导人、各行各业、特别是金融行业的共识。金融风险管理研究所隶属于山东省金融信息工程技术研究中心，依托山东财经大学的学科和科研优势，致力于金融风险的识别、度量和控制的理论与应用的研究。研究所致力于学以致用，充分利用自身在产学研合作方面的独特优势，连接企业与高校，促进科技成果的研究、转化与应用。', 0, 0, '2012-04-21 01:11:03', '2012-04-21 01:07:47');
